INSERT INTO `rex_module` VALUES 
  (1,NULL,'YForm Formbuilder','<?php\r\n\r\n/**\r\n * yform.\r\n *\r\n * @author jan.kristinus[at]redaxo[dot]org Jan Kristinus\r\n * @author <a href=\"http://www.yakamara.de\">www.yakamara.de</a>\r\n */\r\n\r\n// module:yform_basic_output\r\n// v1.1\r\n//--------------------------------------------------------------------------------\r\n\r\n$yform = new rex_yform();\r\nif (\'REX_VALUE[7]\' == 1) {\r\n    $yform->setDebug(true);\r\n}\r\n$form_data = \'REX_VALUE[id=3 output=html]\';\r\n$form_data = trim(rex_yform::unhtmlentities($form_data));\r\n$yform->setObjectparams(\'form_action\', rex_getUrl(REX_ARTICLE_ID, REX_CLANG_ID));\r\n$yform->setFormData($form_data);\r\n\r\n// action - showtext\r\nif (\'REX_VALUE[id=6]\' != \'\') {\r\n    $html = \'0\'; // plaintext\r\n    if (\'REX_VALUE[11]\' == 1) {\r\n        $html = \'1\'; // html\r\n    }\r\n\r\n    $e3 = \'\';\r\n    $e4 = \'\';\r\n    if ($html == \'0\') {\r\n        $e3 = \'<div class=\"alert alert-success\">\';\r\n        $e4 = \'</div>\';\r\n    }\r\n\r\n    $t = str_replace(\'<br />\', \'\', rex_yform::unhtmlentities(\'REX_VALUE[6]\'));\r\n    $yform->setActionField(\'showtext\', [$t, $e3, $e4, $html]);\r\n}\r\n\r\n$form_type = \'REX_VALUE[1]\';\r\n\r\n// action - email\r\nif ($form_type == \'1\' || $form_type == \'2\') {\r\n    $mail_from = (\'REX_VALUE[2]\' != \'\') ? \'REX_VALUE[2]\' : rex::getErrorEmail();\r\n    $mail_to = (\'REX_VALUE[12]\' != \'\') ? \'REX_VALUE[12]\' : rex::getErrorEmail();\r\n    $mail_subject = \'REX_VALUE[4]\';\r\n    $mail_body = str_replace(\'<br />\', \'\', rex_yform::unhtmlentities(\'REX_VALUE[5]\'));\r\n    $yform->setActionField(\'email\', [$mail_from, $mail_to, $mail_subject, $mail_body]);\r\n}\r\n\r\n// action - db\r\nif ($form_type == \'0\' || $form_type == \'2\') {\r\n    $yform->setObjectparams(\'main_table\', \'REX_VALUE[8]\');\r\n\r\n    if (\'REX_VALUE[10]\' != \'\') {\r\n        $yform->setObjectparams(\'getdata\', true);\r\n    }\r\n\r\n    $yform->setActionField(\'db\', [\'REX_VALUE[8]\', $yform->objparams[\'main_where\']]);\r\n}\r\n\r\necho $yform->getForm();\r\n','<?php\r\n\r\n/**\r\n * yform\r\n * @author jan.kristinus[at]redaxo[dot]org Jan Kristinus\r\n * @author <a href=\"http://www.yakamara.de\">www.yakamara.de</a>\r\n */\r\n\r\n// module:yform_basic_input\r\n// v1.1\r\n// --------------------------------------------------------------------------------\r\n\r\n// DEBUG SELECT\r\n////////////////////////////////////////////////////////////////////////////////\r\n$dbg_sel = new rex_select();\r\n$dbg_sel->setName(\'REX_INPUT_VALUE[7]\');\r\n$dbg_sel->setAttribute(\'class\', \'form-control\');\r\n$dbg_sel->addOption(\'inaktiv\',\'0\');\r\n$dbg_sel->addOption(\'aktiv\',\'1\');\r\n$dbg_sel->setSelected(\'REX_VALUE[7]\');\r\n$dbg_sel = $dbg_sel->get();\r\n\r\n\r\n// TABLE SELECT\r\n////////////////////////////////////////////////////////////////////////////////\r\n$gc = rex_sql::factory();\r\n$gc->setQuery(\'SHOW TABLES\');\r\n$tables = $gc->getArray();\r\n$tbl_sel = new rex_select();\r\n$tbl_sel->setName(\'REX_INPUT_VALUE[8]\');\r\n$tbl_sel->setAttribute(\'class\', \'form-control\');\r\n$tbl_sel->addOption(\'Keine Tabelle ausgewählt\', \'\');\r\nforeach ($tables as $key => $value) {\r\n  $tbl_sel->addOption(current($value), current($value));\r\n}\r\n$tbl_sel->setSelected(\'REX_VALUE[8]\');\r\n$tbl_sel = $tbl_sel->get();\r\n\r\n\r\n// PLACEHOLDERS\r\n////////////////////////////////////////////////////////////////////////////////\r\n$yform = new rex_yform;\r\n$form_data = \'REX_VALUE[3]\';\r\n$form_data = trim(str_replace(\'<br />\',\'\',rex_yform::unhtmlentities($form_data)));\r\n$yform->setFormData($form_data);\r\n$yform->setRedaxoVars(REX_ARTICLE_ID,REX_CLANG_ID);\r\n$placeholders = \'\';\r\nif(\'REX_VALUE[3]\'!=\'\') {\r\n  $ignores = array(\'html\',\'validate\',\'action\');\r\n  $placeholders .= \'\r\n        <div id=\"yform-js-formbuilder-placeholders\">\r\n            <h3>Platzhalter: <span>[<a href=\"#\" id=\"yform-js-formbuilder-placeholders-help-toggler\">?</a>]</span></h3>\'.PHP_EOL;\r\n  foreach($yform->objparams[\'form_elements\'] as $e) {\r\n    if(!in_array($e[0],$ignores) && isset($e[1])) {\r\n      $label = (isset($e[2]) && $e[2] != \'\') ? $e[2] . \': \' : \'\';\r\n      $placeholders .= \'<code>\' . $label . \'###\'.$e[1].\'###</code> \'.PHP_EOL;\r\n    }\r\n  }\r\n  $placeholders .= \'\r\n            <ul id=\"yform-js-formbuilder-placeholders-help\">\r\n                <li>Die Platzhalter ergeben sich aus den obenstehenden Felddefinitionen.</li>\r\n                <li>Per Klick können einzelne Platzhalter in den Mail-Body kopiert werden.</li>\r\n                <li>Aktualisierung der Platzhalter erfolgt über die Aktualisierung des Moduls.</li>\r\n            </ul>\r\n        </div>\'.PHP_EOL;\r\n}\r\n\r\n\r\n// OTHERS\r\n////////////////////////////////////////////////////////////////////////////////\r\n$row_pad = 1;\r\n\r\n$sel = \'REX_VALUE[1]\';\r\n$db_display   = ($sel==\'\' || $sel==1) ?\' style=\"display:none\"\':\'\';\r\n$mail_display = ($sel==\'\' || $sel==0) ?\' style=\"display:none\"\':\'\';\r\n\r\n?>\r\n\r\n<div id=\"yform-formbuilder\">\r\n  <fieldset class=\"form-horizontal\">\r\n    <legend>Formular</legend>\r\n    <div class=\"form-group\">\r\n      <label class=\"col-md-2 control-label text-left\">Debug Modus</label>\r\n      <div class=\"col-md-10\">\r\n        <div class=\"yform-select-style\">\r\n          <?= $dbg_sel; ?>\r\n        </div>\r\n      </div>\r\n    </div>\r\n    <div class=\"form-group\">\r\n      <label class=\"col-md-2 control-label\" for=\"yform-formbuilder-definition\">Felddefinitionen</label>\r\n      <div class=\"col-md-10\">\r\n        <textarea class=\"form-control\" style=\"font-family: monospace;\" id=\"yform-formbuilder-definition\" name=\"REX_INPUT_VALUE[3]\" rows=\"<?php echo (count(explode(\"\\r\",\'REX_VALUE[3]\'))+$row_pad);?>\">REX_VALUE[3]</textarea>\r\n      </div>\r\n    </div>\r\n    <div class=\"form-group\">\r\n      <label class=\"col-md-2 control-label\">Verfügbare Feldklassen</label>\r\n      <div class=\"col-md-10\">\r\n        <div id=\"yform-formbuilder-classes-showhelp\"><?= rex_yform::showHelp(); ?></div>\r\n      </div>\r\n    </div>\r\n    <div class=\"form-group\">\r\n      <label class=\"col-md-2 control-label\">Meldung bei erfolgreichen Versand</label>\r\n      <div class=\"col-md-10\">\r\n        <label class=\"radio-inline\">\r\n          <input type=\"radio\" name=\"REX_INPUT_VALUE[11]\" value=\"0\"<?php if(\"REX_VALUE[11]\" == \'0\') echo \' checked\'; ?> /> Plaintext\r\n        </label>\r\n        <label class=\"radio-inline\">\r\n          <input type=\"radio\" name=\"REX_INPUT_VALUE[11]\" value=\"1\"<?php if(\"REX_VALUE[11]\" == \'1\') echo \' checked\'; ?> /> HTML\r\n        </label>\r\n      </div>\r\n      <div class=\"col-md-offset-2 col-md-10\">\r\n        <textarea class=\"form-control\" name=\"REX_INPUT_VALUE[6]\" rows=\"<?php echo (count(explode(\"\\r\",\'REX_VALUE[6]\'))+$row_pad);?>\">REX_VALUE[6]</textarea>\r\n      </div>\r\n    </div>\r\n  </fieldset>\r\n\r\n  <fieldset class=\"form-horizontal\">\r\n    <legend>Vordefinierte Aktionen</legend>\r\n\r\n    <div class=\"form-group\">\r\n      <label class=\"col-md-2 control-label\">Bei Submit</label>\r\n      <div class=\"col-md-10\">\r\n        <div class=\"yform-select-style\">\r\n          <select class=\"form-control\" id=\"yform-js-formbuilder-action-select\" name=\"REX_INPUT_VALUE[1]\" size=\"1\">\r\n            <option value=\"\"<?php if(\"REX_VALUE[1]\" == \"\")  echo \" selected \"; ?>>Nichts machen (actions im Formular definieren)</option>\r\n            <option value=\"0\"<?php if(\"REX_VALUE[1]\" == \"0\") echo \" selected \"; ?>>Nur in Datenbank speichern</option>\r\n            <option value=\"1\"<?php if(\"REX_VALUE[1]\" == \"1\") echo \" selected \"; ?>>Nur E-Mail versenden</option>\r\n            <option value=\"2\"<?php if(\"REX_VALUE[1]\" == \"2\") echo \" selected \"; ?>>E-Mail versenden und in Datenbank speichern</option>\r\n          </select>\r\n        </div>\r\n      </div>\r\n    </div>\r\n  </fieldset>\r\n\r\n  <fieldset class=\"form-horizontal\" id=\"yform-js-formbuilder-mail-fieldset\"<?php echo $mail_display;?> >\r\n    <legend>E-Mail-Versand:</legend>\r\n\r\n    <div class=\"form-group\">\r\n      <label class=\"col-md-2 control-label\">Absender</label>\r\n      <div class=\"col-md-10\">\r\n        <input class=\"form-control\" type=\"text\" name=\"REX_INPUT_VALUE[2]\" value=\"REX_VALUE[2]\" />\r\n      </div>\r\n    </div>\r\n    <div class=\"form-group\">\r\n      <label class=\"col-md-2 control-label\">Empfänger</label>\r\n      <div class=\"col-md-10\">\r\n        <input class=\"form-control\" type=\"text\" name=\"REX_INPUT_VALUE[12]\" value=\"REX_VALUE[12]\" />\r\n      </div>\r\n    </div>\r\n    <div class=\"form-group\">\r\n      <label class=\"col-md-2 control-label\">Subject</label>\r\n      <div class=\"col-md-10\">\r\n        <input class=\"form-control\" type=\"text\" name=\"REX_INPUT_VALUE[4]\" value=\"REX_VALUE[4]\" />\r\n      </div>\r\n    </div>\r\n    <div class=\"form-group\">\r\n      <label class=\"col-md-2 control-label\">Mailbody</label>\r\n      <div class=\"col-md-10\">\r\n        <textarea class=\"form-control\" id=\"yform-js-formbuilder-mail-body\" name=\"REX_INPUT_VALUE[5]\" rows=\"<?php echo (count(explode(\"\\r\",\'REX_VALUE[5]\'))+$row_pad);?>\">REX_VALUE[5]</textarea>\r\n        <div class=\"help-block\">\r\n          <?php echo $placeholders;?>\r\n        </div>\r\n      </div>\r\n    </div>\r\n\r\n  </fieldset>\r\n\r\n  <fieldset class=\"form-horizontal\" id=\"yform-js-formbuilder-db-fieldset\"<?php echo $db_display;?> >\r\n    <legend>Datenbank Einstellungen</legend>\r\n\r\n    <div class=\"form-group\">\r\n      <label class=\"col-md-2 control-label\">Tabelle wählen <span>[<a href=\"#\" id=\"yform-js-formbuilder-db-help-toggler\">?</a>]</span></label>\r\n      <div class=\"col-md-10\">\r\n        <div class=\"yform-select-style\">\r\n          <?= $tbl_sel; ?>\r\n        </div>\r\n        <div class=\"help-block\">\r\n          <ul id=\"yform-js-formbuilder-db-help\">\r\n            <li>Hier werden die Daten des Formular hineingespeichert</li>\r\n          </ul>\r\n        </div>\r\n      </div>\r\n    </div>\r\n  </fieldset>\r\n\r\n</div>\r\n\r\n<p id=\"yform-formbuilder-info\"><?=  rex_addon::get(\'yform\')->getName() . \' \' . rex_addon::get(\'yform\')->getVersion() ?></p>\r\n\r\n<script type=\"text/javascript\">\r\n  <!--\r\n  (function($){\r\n\r\n    // AUTOGROW BY ROWS\r\n    $(\'#yform-formbuilder textarea\').keyup(function(){\r\n      var rows = $(this).val().split(/\\r?\\n|\\r/).length + <?php echo $row_pad;?>;\r\n      $(this).attr(\'rows\',rows);\r\n    });\r\n\r\n    // TOGGLERS\r\n    $(\'#yform-js-formbuilder-placeholders-help-toggler\').click(function(e){\r\n      e.preventDefault();\r\n      $(\'#yform-js-formbuilder-placeholders-help\').toggle(50);return false;\r\n    });\r\n    $(\'#yform-js-formbuilder-where-help-toggler\').click(function(e){\r\n      e.preventDefault();\r\n      $(\'#yform-js-formbuilder-where-help\').toggle(50);return false;\r\n    });\r\n    $(\'#yform-js-formbuilder-db-help-toggler\').click(function(e){\r\n      e.preventDefault();\r\n      $(\'#yform-js-formbuilder-db-help\').toggle(50);return false;\r\n    });\r\n\r\n\r\n    // INSERT PLACEHOLDERS\r\n    $(\'#yform-js-formbuilder-placeholders code\').click(function(){\r\n      newval = $(\'#yform-js-formbuilder-mail-body\').val()+\' \'+$(this).html();\r\n      $(\'#yform-js-formbuilder-mail-body\').val(newval);\r\n      $(this).addClass(\'text-muted\');\r\n    });\r\n\r\n    // TOGGLE MAIL/DB PANELS\r\n    $(\'#yform-js-formbuilder-action-select\').change(function(){\r\n      switch($(this).val()){\r\n        case \'\':\r\n          $(\'#yform-js-formbuilder-db-fieldset\').hide(0);\r\n          $(\'#yform-js-formbuilder-mail-fieldset\').hide(0);\r\n          break;\r\n        case \'1\':\r\n          $(\'#yform-js-formbuilder-db-fieldset\').hide(0);\r\n          $(\'#yform-js-formbuilder-mail-fieldset\').show(0);\r\n          break;\r\n        case \'0\':\r\n          $(\'#yform-js-formbuilder-db-fieldset\').show(0);\r\n          $(\'#yform-js-formbuilder-mail-fieldset\').hide(0);\r\n          break;\r\n        case \'2\':\r\n        case \'3\':\r\n          $(\'#yform-js-formbuilder-db-fieldset\').show(0);\r\n          $(\'#yform-js-formbuilder-mail-fieldset\').show(0);\r\n          break;\r\n      }\r\n    });\r\n\r\n  })(jQuery)\r\n  //-->\r\n</script>\r\n','0000-00-00 00:00:00','','2021-03-20 16:15:59','Thomas',NULL,0),
  (2,'text_markdown','Textabschnitt','<?php\n  $firstStage = str_replace(\"\\n\",\'<br>\',markitup::parseOutput (\'markdown\', \'REX_VALUE[id=1 output=\"html\"]\'));\n  echo str_replace(\'<br><\',\'<\',$firstStage);\n?>','<fieldset class=\"form-horizontal\">\n  <div class=\"form-group\">\n    <label class=\"col-sm-2 control-label\" for=\"value-1\">VALUE 1</label>\n    <div class=\"col-sm-10\">\n      <textarea class=\"form-control markitupEditor-markdown_full\" id=\"value-1\" name=\"REX_INPUT_VALUE[1]\">REX_VALUE[1]</textarea>\n    </div>\n  </div>\n</fieldset>','2020-05-22 16:37:28','Thomas','2021-03-20 16:15:59','Thomas',NULL,0),
  (4,'tth_view_list_letter','Liste nach Anfangsbuchstaben','<?php\nif (rex::isBackend()) {\n	?>\n	<p>Artikel für Begriffe: <strong>REX_LINK[id=1 output=name]</strong> (ID: REX_LINK[id=1 output=id])</p>\n	<?php\n}\n?>\n\n<p>\n<a id=\"marker-letters\"></a>\n<?php\n\n$targetArticleId = \'REX_LINK[id=1 output=id]\';\n\nfor($i = 0; $i <= 25; $i++ ) {\n	$letter = chr($i+65);\n	if (23 !== $i && 24 !== $i) {\n		echo \'<a href=\"\'.rex_getUrl(\'\',\'\',array( \'startletter\' => $letter)).\'#marker-letters\" class=\"btn btn-primary\">\'.$letter.\'</a> \';\n	}\n}\n\n$startLetter = rex_request(\'startletter\');\nif ($startLetter) {\n	$startLetter = strtoupper($startLetter);\n	if (!isset($sql)) $sql = rex_sql::factory();\n	if (!isset($vm)) $vm = new \\kwd\\tth\\ViewFormatter($sql, \'rex_getUrl\');\n\n	$entities = $vm->getTableManagerInstance()->findEntities($startLetter.\'*\');\n	if ($entities) {\n		echo \'<hr>\'.$vm->getEntityLinkList($entities, $targetArticleId);\n	}\n	else {\n		echo \'Keine Begriffe gefunden\';\n	}\n}\n?>\n</p>','<p>Artikel, der Detailansicht der <em>Begriffe</em> enthält:<br>\nREX_LINK[id=1 widget=1]\n</p>\n','2020-05-23 20:27:10','Thomas','2021-03-22 15:01:16','Thomas',NULL,0),
  (5,'tth_view_item','Detailansicht','<!-- you will need to use bootstrap rows/cols -->\n<?php\n	if (rex::isBackend()) {\n		// !!! make general function for \"backend-page link\"\n		?>\n		<p>Ergebnisse nur im Frontend.</p>\n		<p>Bei fehlender ID des Begriffs wird eine Warnung ausgegeben.</p>\n		<p>Die Ziel-Seite der globalen Suche sollte dieses Modul enthalten. Da bei Suchmuster eine Auswahlliste erzeugt wird, wenn das Suchmuster auf mehrere Begriffe passt.</p>\n		<p>Artikel für Quellen: <strong>REX_LINK[id=1 output=name]</strong> (ID: REX_LINK[id=1 output=id])</p>\n		<p>Artikel für Schlagwörter:  <strong>REX_LINK[id=2 output=name]</strong> (ID: REX_LINK[id=2 output=id])</p>\n		<?php\n	}\n	else {\n		?>\n		<div class=\"row detailed-view\">\n		<div class=\"col\">\n		<?php\n\n		// !!! how pack in function?\n		if (!isset($sql)) $sql = rex_sql::factory();\n		if (!isset($vm)) $vm = new \\kwd\\tth\\ViewFormatter($sql, \'rex_getUrl\'); \n		$tm = $vm->getTableManagerInstance();\n		$descriptorId = $tm->getDescriptorId();\n		$facetteId = $tm->getFacetId();\n		\n		\n		// !!! why not settable in module INPUT?\n		// ! localhost = 21, on kwd = 20 \n		$glossarArticleId = rex_config::get(\'tth\', \'article_glossar\');\n		if (!$glossarArticleId) {\n			$glossarArticleId = 20;\n			rex_config::set(\'tth\', \'article_glossar\', $glossarArticleId);\n		}\n		// !!! make $vm->setGlossarArticleId()\n\n		$filterArticleId = rex_config::get(\'tth\', \'article_filter\');\n		// ! localhost = ?, on kwd = 5 \n		if (!$filterArticleId) {\n			$filterArticleId = 5;\n			rex_config::set(\'tth\', \'article_filter\', $filterArticleId);\n		}\n		\n		$searchResultList = \'\';\n		$id = 0;\n\n		// first check search results,\n		$wSearch = rex_escape(rex_request(\'wordlistsearch\',\'string\'));\n		if ($wSearch) {\n			$searchPattern = rex_escape($wSearch);\n			if ($searchPattern) {\n				// !!! Problem wenn Anfuehrungszeichen in gespeichertem Wort\n				\n				$searchPattern = \n					str_replace(\"\'\",\"\",\n					str_replace(\"`\",\"\",\n					str_replace(\'\"\',\'\',\n					str_replace(\"\\n\",\'\',\n					str_replace(\';\',\'\',\n					$searchPattern\n				)))));\n\n				if (false === strpos($searchPattern,\'*\')) {\n					// search for word as is \n					// ! note that can still be more than 1 because of possible entities with exact same name\n					$singleEntities= $tm->findEntities($searchPattern, true); // true means strictly the word/pattern\n					if (1 === count($singleEntities)) {\n						// redirect is cleaner (HTTP url consistency)\n						rex_redirect(\'\',\'\',[\'begriff_id\' => $singleEntities[0][\'id\']]);\n					}\n					else {\n						// ! change pattern to always have PART of word when no *\n						$searchPattern = \'*\'.$searchPattern.\'*\';\n					}\n				}\n				\n				$entities = $tm->findEntities($searchPattern);\n				if ($entities) {\n					$searchResultList = $vm->getEntityLinkList($entities);\n				}\n			}\n		}\n\n		if (!$id) $id = rex_request(\'begriff_id\',\'int\');\n		if ($id) {\n			$sourcesArticleId = \'REX_LINK[id=1 output=id]\';\n			$tagsArticleId = \'REX_LINK[id=2 output=id]\';\n			if (!$sourcesArticleId) $sourcesArticleId = \'REX_ARTICLE_ID\';\n									\n			$r = $tm->getSingleEntity($id); // getSingleEntity returns empty array when not found\n			if ($r) { \n				$html = \'\';\n\n				if ($tm->checkTruthyWord($r[\'bearbeiten\'])) {\n					$html .= rex_view::warning(\'Dieser Eintrag (ID: \'.$r[\'id\'].\') muss noch überarbeitet werden.\');\n				}\n\n				$html .= \'<table class=\"table table-responsive\">\';\n\n				// row 1\n				$html .= \'<tr>\';\n				$html .= \'<td>\';\n				if ($r[\'begriffsstatus_id\'] == $facetteId) {\n					$html .= \'<strong>Facette!</strong>\';\n				}\n				else {\n					// !!! join in SQL, see above for \'sprache\', \'region\', etc.\n					$html .= \'Facette: [facette_id]\';\n					\n				}\n\n				// ! depending on anchor correctly set in DB  (glossar entry)\n				// !!! should read all anchors from DB \n				$html .= \'  <a href=\"\'.rex_getUrl($glossarArticleId).\'#facette\">(?)</a></td>\';\n				$html .= \'<td>\';\n				$html .= $vm->getRelationLinkList(\'supers\', $id);\n				// 						Ein weiterer Vorteil ist, dass die Platzhalter wiederholt werden können, ohne mehr Argumente im Code hinzufügen zu müssen.\n\n				// <?php\n				// $format = \'Der %2$s enthält %1$d Affen.\n				//            %1$d Affen sind ziemlich viel für einen %2$s.\';\n				// ! must be \"unterbegriffe\" reverse to show generated \"oberbegriffe\"\n				$html .= \'<br>\'.$vm->getReverseRelationLinkList(\'subs\', $id);\n				$html .= \'</td>\';\n				$html .= \'<td></td>\';\n				$html .= \'</tr>\';\n\n				// row 2\n				$html .= \'<tr>\';\n				$html .= \'<td>\';\n				\n				// !!! list facettes when current is facette\n				if ($r[\'begriffsstatus_id\'] == $facetteId) {\n					$facettes = str_replace(\'a>, \',\'a><br>\',$vm->getEntityLinkListByFieldValue(\'state\',$facetteId));\n					if ($facettes) {\n						$html .= \'Facetten:<br>\'.$facettes;\n					}\n				}\n				else {\n					// ! we must check $r[\'benutze\'] because benutze==0 also produces link\n					$benutzeBegriff = \'\';\n					if ($r[\'benutze\']) $benutzeBegriff = trim($vm->getLink(\'begriff_id\', $r[\'benutze\'], $r[\'benutze_begriff\']));\n					\n					$descriptorLink = \'  <a href=\"\'.rex_getUrl($glossarArticleId).\'#deskriptor\">(?)</a>\';\n					\n					if ($r[\'begriffsstatus_id\'] == $descriptorId) {\n						\n						$html .= \'<strong>Deskriptor!</strong>\'.$descriptorLink.\'<br>\';\n						if ($benutzeBegriff) {\n							$html .= \'<em>Fehler: Deskriptor verweist auf Deskriptor.<br>\';\n						}\n					}\n					else {\n						$html .= \'Deskriptor:\'.$descriptorLink.\'<br>\';\n						if (!trim($benutzeBegriff)) {\n							$html .= \'(nicht gesetzt!)\';\n						}\n					}\n					\n					// ! depending on anchor correctly set in DB  (glossar entry)\n					// !!! should read all anchors from DB \n					\n					$html .= $benutzeBegriff.\'<br>\'; // ! always\n\n					// !!! check if is descriptor,\n					//     if not, get class of descriptor id,\n					//     in both cases we should get data about begriffsstatus of each entity in list to mark \n					//     linking errors.\n					$html .= \'Äquivalenzklasse:\'.$vm->getGlossarLink(\'aequivalenzklasse\',$glossarArticleId).\'<br>\';		\n					$html .= $vm->getEntityLinkListByFieldValue(\'descriptor\',$id);\n					if ($r[\'benutze\']) {\n						$html .= $vm->getEntityLinkListByFieldValue(\'descriptor\',$r[\'benutze\']);\n						$html .= \'<br><span class=\"small\"><em>(falls hier Dopplungen auftreten, sind unzulässigerweise Deskriptoren verkettet.)</em></span>\';\n					}\n\n					if ($r[\'benutzt_fuer\']) {\n						// make the whole logic in TableManager\n						$ids = $r[\'benutzt_fuer\'];\n						if (false !== strpos($ids, \',\')) $rawIds = explode(\',\', $ids);\n						else if (false !== strpos($ids, \';\')) $rawIds = explode(\';\', $ids);						\n						$html .= \'<hr><span class=\"small\">Manuelle Synonyme (\"benutzt_fuer\"): \'\n							.$vm->getEntityLinkList($tm->getEntitiesFromIdList($rawIds))\n							.\'</span>\';\n					}\n				}\n					\n				$html .\'</td>\';\n\n				$html .= \'<td class=\"entity-center\"><h2>\'.$r[\"begriff\"].\'</h2>\';\n\n				$html .= $vm->getMarkDownText(\'markitup::parseOutput\',rex_addon::get(\'markitup\'), $r[\'definition\']);\n				$html .= \'<hr>\';\n\n				// !!! $html .= \'Sprache: \'.$vm->getFilterCrossLink($r[\'sprache\'], \'sprache_id\', $r[\'sprache_id\']).\', \';\n				$html .= \'Sprache: <a href=\"\'.rex_getUrl($filterArticleId,\'\',[\'sprache_id\'=>$r[\'sprache_id\']]).\'\">\'.$r[\'sprache\'].\'</a>, \';\n				$html .= \'Sprachstil: <a href=\"\'.rex_getUrl($filterArticleId,\'\',[\'sprachstil_id\'=>$r[\'sprachstil_id\']]).\'\">\'.$r[\'sprachstil\'].\'</a>, \';\n				$html .= \'Sprachregion: <a href=\"\'.rex_getUrl($filterArticleId,\'\',[\'region_id\'=>$r[\'region_id\']]).\'\">\'.$r[\'region\'].\'</a>\';\n\n\n				$html .= \'</td>\';\n				$html .= \'<td>\';\n\n				$html .= \'Äquivalente (manuell):\';\n				$html .= $vm->getRelationLinkList(\'equivalents\', $id);\n				// !!! cool would be \'br\' only when needed -> make sub function for all reverse addition\n				// !!! idea for getBidirectionalRelationLinkList see `table_manager.php`\n				$html .= \'<br>\'.$vm->getReverseRelationLinkList(\'equivalents\', $id);\n\n				$html .= \'Verwandte:<br>\';\n				$html .= $vm->getRelationLinkList(\'relatives\', $id);\n				$html .= \'<br>\'.$vm->getReverseRelationLinkList(\'relatives\', $id);\n				$html .= \'</td>\';\n				$html .= \'</tr>\';\n\n				// row 3\n				$html .= \'<tr>\';\n				$html .= \'<td></td>\';\n				$html .= \'<td>\';\n				$html .= $vm->getRelationLinkList(\'subs\', $id);\n				// ! must be \"unterbegriffe\" reverse to show generated \"oberbegriffe\"\n				$html .= \'<br>\'.$vm->getReverseRelationLinkList(\'supers\', $id);\n				$html .= \'</td>\';\n				$html .= \'<td>Schlagwörter:<br>\';\n				$html .= $vm->getForeignEntriesLinkList(\'tags\', $id, $tagsArticleId);\n				$html .= \'</td>\';\n				$html .= \'</tr>\';\n\n				$html .= \'</table>\';\n				echo $html;\n\n				// !!! type check!\n				if ($r[\'begriffsstatus_id\'] == $facetteId) {\n					echo rex_view::warning(\'Dies ist eine \"Facette\". Es ist somit ein Oberbegriff der höchsten Hierarchieebene und kann nicht als normale Entität behandelt werden.\');\n				}\n\n				$html = \'<table class=\"table table-responsive\">\';\n				\n				// $html .= $vm->getRow(\'Sprache\', $r[\'sprache\']);\n				// $html .= $vm->getRow(\'Sprachstil\', $r[\'sprachstil\']);\n				// $html .= $vm->getRow(\'Region\', ($r[\'region\']) ? $r[\'region\'] : \"\");\n				$html .= $vm->getRow(\'ID\', $r[\'id\']);\n				$html .= $vm->getRow(\'Begriffcode\', $r[\'code\']);\n				$html .= $vm->getRow(\'Begriffs-Status\',\'<a href=\"\'\n				.rex_getUrl(\n					$filterArticleId,\n					\'\',\n					[ \'begriffsstatus_id\' => $r[\'begriffsstatus_id\'] ]\n				)\n				.\'\">\'.$r[\'status\']).\'</a>\';\n				// ! redaxo file list is *comma* separated\n				if ($r[\'bild\']) {\n					// ! separator \',\' is determined by redaxo\n					$images = explode(\',\',$r[\'bild\']);\n					$imgHTML = \'\';\n					foreach ($images as $img) {\n						$imgHTML .= \'<img src=\"index.php?rex_media_type=tth_horizontal_list&rex_media_file=\'.$img.\'\">\';\n					}\n					$html .= $vm->getRow(\'Bilder\',$imgHTML);\n				}\n				\n				// !!! how make view DONT know model?\n				$html .= $vm->getRow(\n					\'Grobgliederung\',\n					$vm->getRelationLinkList(\'structuring\', $id)\n				);\n\n				$html .= $vm->getRow(\n					\'Begriffe, bei denen Grobgliederung auf diesen Begriff verweist\',\n					$vm->getReverseRelationLinkList(\'structuring\', $id)\n				);\n									\n				// !!! use small def from Bootstrap\n				// $html .= $vm->getRow(\'Schlagwörter\',$tagList.\'<br><small></small>\');\n				\n				// ! first link\n				// $html .= $vm->getRow(\'Synonym von (Benutze)\',$vm->getLink(\'begriff_id\', $r[\'benutze\'], $r[\'benutze_begriff\']).\'<br><small>dies ist der Desriptor und damit Name der <em>Äquivalenzklasse</em></small>\');\n					\n				// !!! use small def from Bootstrap\n				// $syns = $vm->getLinkList($synonyms, \'begriff_id\', \'begriff\');\n				// $html .= $vm->getRow(\'Deskriptor von (Benutzt für)\',$syns.\'<br><small>diese sind zusammen mit dem Begriff \"\'.$r[\'begriff\'].\'\" selbst die <em>Äquivalenzklasse</em></small>\');\n				\n				// if ($r[\'quelle_seite\']) $html .= $vm->getRow(\'Seite in Quelle\',$r[\'quelle_seite\']);\n				\n				$html .= $vm->getRow(\'Scoped Notes\',$r[\'notes\']);\n				$html .= $vm->getRow(\'Kategorie\', \'<a href=\"\'.rex_getUrl($filterArticleId,\'\',[\'category_value\'=>$r[\'kategorie\']]).\'\">\'.$vm->getTruthyWord($r[\'kategorie\']).\'</a>\');\n				// ;\n				$html .= $vm->getRow(\'Veröffentlichen?\',$vm->getTruthyWord($r[\'veroeffentlichen\']));\n				$html .= $vm->getRow(\'Noch bearbeiten\',\'<a href=\"\'.rex_getUrl($filterArticleId,\'\',[\'edit_value\'=>$r[\'bearbeiten\']]).\'\">\'.$vm->getTruthyWord($r[\'bearbeiten\']).\'</a>\');\n				\n				$authorText = \'\';\n				// ! the `if` is important because the SQL may still returen the first entry of tth_autoren for some reason when autor_id=\'\'  ! whole data set not returned when no author; need 0 clause in inner join\n				// !!! link to author with autor_id\n				if ($r[\'autor\']) { // is a generated value; and is NULL when not set\n					$authorText .= $r[\'autor\'];\n					// !!! provide link for GND (see code in details of \"Quelle\")\n					if (trim($r[\'gnd\'])) $authorText .= \' (GND: \'.$r[\'gnd\'].\')\';\n				}\n				$html .= $vm->getRow(\'Autor\',$authorText);\n				$html .= \'</table>\';\n				echo $html;\n					\n				// !!! remove after tests because unnessecary doubled sql query\n				// !!! or refactor by using getLinkList directly, but then you\'ll need to query names from TableManager\n				// echo \'<p>new code list of sources: \'.$vm->getForeignEntriesLinkList(\'references\', $id, $sourcesArticleId). \'</p>\';\n				\n				$sourcesArray = $tm->getForeignEntries(\'references\', $id);\n				$html = \'<hr><table class=\"table table-responsive\">\';\n				// make header line of table\n				$html .= \"<thead><tr><th>#</th><th>Quelle</th><th>Seitenzahl</th><th>Bevorzugt?</th></thead>\\n\";\n				$i = 1;\n				foreach($sourcesArray as $s) {\n					$html .= \"<tr>\\n\";\n					$html .= \"<td>$i</td> \";\n					$html .= \"<td>\".$vm->getLink(\'quelle_id\',$s[\'quelle_id\'], \'<div class=\"author-name\">\'.$s[\'kurz\'].\'</div>\', $sourcesArticleId).\"</td> \";\n					// check for any truthy value\n					$html .= \"<td>\".($s[\'seitenzahl\'] ? $s[\'seitenzahl\'] : \'\').\"</td> \";\n					// check for any truthy value\n					$html .= \"<td>\".($s[\'bevorzugt\'] ? \'bevorzugt\' : \'\').\"</td> \";\n					// $html .= \"<td>${s[\'bevorzugt\']}</td> \";\n					$html .= \"</tr>\\n\";\n					$i++;\n				}\n				echo $html.\'</table>\';\n			\n				// write a string with begriff\n				echo \'{{{\'.$r[\'begriff\'].\'}}}\';\n				// rex_config::set(\'tth\',\'current_entity\',$r[\'begriff\']);\n			}\n			else {\n				echo rex_view::warning(\'Eintrag für ID = \'.$id.\' nicht gefunden.\');\n			}\n\n		}\n\n			// !!! check: remove this if when hierarchy is always on other page \n			else if (!rex_request(\'facette_id\')) {\n				if ($wSearch) {\n					echo \'<div class=\"card\"><div class=\"card-body\"><h5 class=\"card-title\">\'.($searchResultList ? \'\' : \'<strong>Keine</strong> \').\'Ergebnisse für \"\'.$wSearch.\'\":</h5><p>\'.$searchResultList.\'</p></div></div>\';\n				}\n				else if (!$id) {\n					// !!! make text editable in module input\n					echo rex_view::warning(\'Kein Begriff ausgewählt.\');\n				}\n			}\n			?>\n	</div>\n</div>\n				<?php\n			} // if rex::isBackend()\n			?>\n','<p>Artikel, der Detailansicht der <em>Literatur-Quellen</em> enthält:</p>\nREX_LINK[id=1 widget=1]\n<p>Artikel, der Übersicht der <em>Schlagwörter</em> enthält:</p>\nREX_LINK[id=2 widget=1]\n','2020-05-25 10:51:48','Thomas','2021-03-22 23:02:47','Thomas',NULL,0),
  (6,'tth_view_source','Quellen-Detailansicht','<?php\n\n$quelleId = rex_escape(rex_request(\'quelle_id\',\'int\')); // the param \'int\' should prevent from dangerous inputs\n// is_numeric is enough sanitize if we just need an id\nif ($quelleId && is_numeric($quelleId)) {\n\n	// !!! how pack in function? not possible\n	if (!isset($sql)) $sql = rex_sql::factory();\n	if (!isset($vm)) $vm = new \\kwd\\tth\\ViewFormatter($sql, \'rex_getUrl\'); \n\n	echo \'Zeige Quelle id \'.$quelleId;\n	$tm = $vm->getTableManagerInstance();\n	$r = $tm->getSingleDataSet(\'sources\', $quelleId);\n	$authors = $tm->getAuthorsForSource($quelleId);\n\n	if ($r) {\n		echo \"<h2>${r[\'kurz\']}</h2>\";\n		?>\n		<table class=\"table table-responsive\">\n		<?php\n		$html = \'\';\n		$html .= $vm->getRow(\'Titel\',$r[\'titel\']);\n		$html .= $vm->getRow(\'Jahr\',$r[\'jahr\']);\n		$html .= $vm->getRow(\'ISBN\',$r[\'isbn\']);\n		$html .= $vm->getRow(\'Kurzform\',$r[\'kurz\']);\n		// !!! make link to details for author table (or general link to table with all authors, still not many)\n		$authorsHtml = \'\';\n		foreach($authors as $a) {\n			$authorsHtml .= \"${a[\'vorname\']} ${a[\'name\']}\";\n			if ($a[\'gnd\']) {\n				// !!! make as config var or sprog/string replacement\n				$authorsHtml .= \" (GND: <a href=\'https://portal.dnb.de/opac.htm?query=${a[\'gnd\']}\'>${a[\'gnd\']}</a>)\";\n			}\n			$authorsHtml .= \", \";\n		}\n		$html .= $vm->getRow(\'Autoren\',$authorsHtml.\' <small>(ID in altem System: \'.$r[\'autor_id\'].\')</small>\');\n\n		echo $html;\n		?>\n		</table>\n		<?php\n	}\n	else {\n		// !!! again use module vars for text template\n		echo rex_view::warning(\'Kein Datensatz gefunden für ID=\'.$quelleId);\n	}\n}\n?>','','2020-05-26 11:03:42','Thomas','2021-03-22 21:34:00','Thomas',NULL,0),
  (7,'tth_view_list_param','Liste nach Parameter (nur Begriffe)','<?php\n// - put all tth_view_list* together for less modules\n// - set params ins module instead for start view\n// - control output by params from url\n\nif (!function_exists(\'printCardWithList\')) {\n		// printCard($vm->getFilteredLinks(\'languages\', rex_vewescape(rex_request(\'sprache_id\')), \'REX_LINK[id=1 output=id]\'));\n\n	function printCardWithList(&$view, $subject, $idName, $displayedFilter = \'Suchwort\') {\n		$html = $view->getFilteredLinks($subject, rex_escape(rex_request($idName)), \'REX_LINK[id=1 output=id]\');\n		if (trim($html)) {\n			$heading = sprintf(substr($html, 0, strpos($html, \':\')), $displayedFilter); \n			$list = substr($html, strpos($html, \':\')+1);\n			// count entries because not returned by getFilteredLinks\n			$count = substr_count($list, \'<a \');\n			?>\n			<div class=\"card\">\n				<div class=\"card-header\"><?=$heading?> (<?=$count?>):</div>\n				<div class=\"card-body\"><?=$list?></div>\n			</div>\n			<?php\n		}\n	}\n}\n\n$detailsArticleId = \'REX_LINK[id=1 output=id]\';\n\nif (!isset($sql)) $sql = rex_sql::factory();\nif (!isset($vm)) $vm = new \\kwd\\tth\\ViewFormatter($sql, \'rex_getUrl\'); \n\n\n// ! we don\'t read \"id names\" from tableManager because can also be different from DB (convention of MODULE code)\nprintCardWithList($vm, \'languages\', \'sprache_id\', \'Sprache\'); // could get easier with SPROG + i18n\nprintCardWithList($vm, \'languagestyles\', \'sprachstil_id\', \'Sprachstil\');\nprintCardWithList($vm, \'regions\', \'region_id\', \'Region\'); \nprintCardWithList($vm, \'states\', \'begriffsstatus_id\', \'Begriffsstatus\');\nprintCardWithList($vm, \'edit\', \'edit_value\', \'Noch bearbeiten\'); \nprintCardWithList($vm, \'is_category\', \'category_value\', \'Kategorie\'); \n\n$quellen = $vm->getTableManagerInstance()->getAllEntries(\'sources\', true);\narray_push($quellen, array( \'id\' => 0, \'kurz\' => \'nicht gesetzt\'));\n\n$source = rex_request(\'quelle_id\');\n\nif ($source || 0 === $source || \'0\' === $source) {\n	$referencesLinkList = \'\';\n	if ($source) {\n		$referencesLinkList = $vm->getEntityLinkListForOuterRelation(\'references\', $source, $detailsArticleId);\n	}\n	else {\n		$referencesLinkList = $vm->getEntityLinkListForUnsetRelation(\'references\', $detailsArticleId);\n	}\n		\n	$sourceName = \'??\';\n	foreach ($quellen as $q) {\n		if ($source == $q[\'id\']) {\n			$sourceName = $q[\'kurz\'];\n		}\n	}\n	\n	?>\n	<p><strong>Begriffe nach Quelle \"<?=$sourceName?>\"</strong> (<?=substr_count($referencesLinkList, \'<a \')?> Einträge):</strong> <?=$referencesLinkList?></p>\n	<?php	\n}\n?>\n<hr>\n<p><strong>Sprache:</strong> <?=$vm->getAllEntriesLinkList(\'languages\', true, true)?></p>\n<p><strong>Region:</strong> <?=$vm->getAllEntriesLinkList(\'regions\', true, true)?></p>\n<p><strong>Sprachstil:</strong> <?=$vm->getAllEntriesLinkList(\'languagestyles\', true, true)?></p>\n<p><strong>Begriffsstatus:</strong> <?=$vm->getAllEntriesLinkList(\'states\', true, true)?></p>\n<?php\n\n// !!! also put in function or method!\n$edit = [\n	[\n		\'id\' => \'FALSE\',\n		\'edit\' => \'nein\'\n	],\n	[\n		\'id\' => \'TRUE\',\n		\'edit\' => \'ja\'\n	],\n	[\n		\'id\' => \'0\',\n		\'edit\' => \'nicht gesetzt\'\n	]\n];\n?>\n<p><strong>Noch bearbeiten:</strong> <?=$vm->getLinkList($edit, \'edit_value\', \'edit\')?></p>\n<?php\n\n// !!! also put in function or method!\n$isCat = [\n	[\n		\'id\' => \'FALSE\',\n		\'is_category\' => \'nein\'\n	],\n	[\n		\'id\' => \'TRUE\',\n		\'is_category\' => \'ja\'\n	],\n	[\n		\'id\' => \'0\',\n		\'is_category\' => \'nicht gesetzt\'\n	]\n];\n?>\n<p><strong>Kategorie:</strong> <?=$vm->getLinkList($isCat, \'category_value\', \'is_category\')?></p>\n<p><strong>Quelle:</strong> <?=$vm->getLinkList($quellen, \'quelle_id\', \'kurz\')?></p>\n<?php\n\n?>','<p>Artikel, der Detailansicht der <em>Begriffe</em> enthält:</p>\nREX_LINK[id=1 widget=1]','2020-05-26 14:44:42','Thomas','2021-03-21 23:29:09','Thomas',NULL,0),
  (8,'tth_view_table','Tabellenansicht','<?php\n	if (!isset($sql)) $sql = rex_sql::factory();\n	if (!isset($vm)) $vm = new \\kwd\\tth\\ViewFormatter($sql, \'rex_getUrl\'); \n\n	$selected = \'REX_VALUE[1]\';\n	$x = explode(\'~\',$selected);\n	$subject = $x[0];\n	$name = $x[1];\n\n	echo \"<h2>$name</h2>\";\n\n	$data = $vm->getTableManagerInstance()->getOuterRelationTableData($subject);\n\n	$html = \'\';\n\n	// !!! need options\n	//     - define white-space: nowrap for some columns OR at least column id as CSS class for each cell\n	//     - hide some cols\n	//     - custom names for field names\n	if (count($data)) {\n\n		$html = \'<table class=\"table table-responsive\">\';\n\n		foreach($data[0] as $key => $value) {\n			$html .= \'<th>\'.$key.\'</th>\';\n		}\n\n		foreach($data as $a) {\n			$html .= \"<tr>\";\n			\n			foreach($a as $key => $value) {			\n				$html .= \'<td>\'.$value.\'</td>\';\n			}\n			\n			$html .= \"</tr>\\n\";\n		}\n\n		$html .= \'</table>\';\n	}\n\n	echo $html;\n?>','Tabelle anzeigen:\n<div class=\"rex-select-style\">\n<select id=\"REX_INPUT_VALUE[1]\" name=\"REX_INPUT_VALUE[1]\" class=\"form-control\">\n<?php\n\n// ! key is convention of TableManager (= meta names as \"subject\" which are mapped to actual DB tables)\n$tables = array(\n	\'authors\' => \'Autoren\',\n	\'states\' => \'Begriffsstati\',\n	\'sources\' => \'Quellen\',\n	\'regions\' => \'Regionen\',\n	\'languages\' => \'Sprachen\',\n	\'languagestyles\' => \'Sprachstile\'\n);\n\n$preSelected = \'REX_VALUE[1]\';\n\nforeach($tables as $key => $value) {\n	echo \"<option value=\'$key~$value\' \".($preSelected == $key.\'~\'.$value ? \" selected=\'selected\'\":\"\").\">$value</option>\\n\";\n}\n?>\n</select>\n</div>','2020-05-26 20:08:16','Thomas','2021-03-20 16:15:59','Thomas',NULL,0),
  (9,'tth_convert_sources','Konvertiere Quellen','<?php if (rex::isBackend()) : ?>\nDer Callback heißt \'convertSourcesCallback\'. Du brauchst einen YForm-Block zum Starten.\n<?php endif; \n\n// !!! remove code, probably not needed again\nif (!function_exists(\'convertSourcesCallback\')) {\n	function convertSourcesCallback($request) {\n		// echo \'absenden erkannt\';\n		// - convert from quellen_idlist and quelle_seite; easier than from created relations table\n		// - preserve order in list\n		// - assume page number to belong to first quelle\n		// - assume first quelle to be \"preferred\"\n		$sql = rex_sql::factory();\n		$query = \"SELECT id,quelle_seite,quellen_idlist FROM tth_wortliste WHERE quellen_idlist != \'\'\";\n		$rows=$sql->getArray($query);\n		echo count($rows) .\" Einträge mit Quellen.\\n\";\n\n		$insertList = \'\';\n		foreach($rows as $r) {\n			$sources = explode(\';\',$r[\'quellen_idlist\']);\n			// echo \', \'.count($sources);\n			$i = 0;\n			$seite = $r[\'quelle_seite\'];\n			if (!$seite) $seite = \"\'\'\";\n\n			foreach($sources as $s) {\n				if (trim($s)) {\n					// echo \'.\';\n					// order: quelle_id, seitenzahl, bevorzugt, begriff_id\n					// $insertList .= \"(\'.$s.\',\'.$node.\"),\\n\"; check if \\n needed!\n					$insertList .= \"($s, \".($i === 0 ? $seite : \"\'\'\").\", \".($i === 0 ? \"\'TRUE\'\" : \"\'\'\").\", ${r[\'id\']}), \";\n					$i++;\n				}\n			}\n		}\n\n		// remove last comma!\n		$insertList = substr($insertList,0,strrpos($insertList,\',\'));\n		$query = \'INSERT INTO tth_quellenangaben (quelle_id, seitenzahl, bevorzugt, begriff_id) VALUES \'.$insertList;\n		echo \'<h3>SQL-Befehl</h3><p>\'.$query.\'</p>\';\n		\n		// disabled for security\n		// $sql->setQuery($query);\n	}\n}\n\nif (rex_post(\'FORM\')) {\n}\nelse {\n	$sql = rex_sql::factory();\n	// \"SUM\" can be passed a simpler expression than \"COUNT\"\n	$query = \"SELECT COUNT(id) AS numEntities, SUM(quellen_idlist != \'\') AS numSources, SUM(quellen_idlist LIKE \'%;%\') AS numMultipleSources, SUM(quelle_seite != \'\') AS numPageNumbers FROM tth_wortliste\";\n	$sql->setQuery($query);\n	$rows=$sql->getArray();\n	$d = $rows[0]\n?>\n<p>\n	Einträge: <?=$d[\'numEntities\']?><br>\n	Einträge mit Quellen: <?=$d[\'numSources\']?><br>\n	Einträge mit mehreren Quellen: <?=$d[\'numMultipleSources\']?><br>\n	Einträge mit Seitenzahlen: <?=$d[\'numPageNumbers\']?>\n</p>\n\n<?php } ?>','Keine Einstellungen einfach speichern.','2020-06-10 22:20:45','Thomas','2021-03-22 22:28:16','Thomas',NULL,0),
