INSERT INTO `rex_module` VALUES 
  (1,NULL,'YForm Formbuilder','<?php\r\n\r\n/**\r\n * yform.\r\n *\r\n * @author jan.kristinus[at]redaxo[dot]org Jan Kristinus\r\n * @author <a href=\"http://www.yakamara.de\">www.yakamara.de</a>\r\n */\r\n\r\n// module:yform_basic_output\r\n// v1.1\r\n//--------------------------------------------------------------------------------\r\n\r\n$yform = new rex_yform();\r\nif (\'REX_VALUE[7]\' == 1) {\r\n    $yform->setDebug(true);\r\n}\r\n$form_data = \'REX_VALUE[id=3 output=html]\';\r\n$form_data = trim(rex_yform::unhtmlentities($form_data));\r\n$yform->setObjectparams(\'form_action\', rex_getUrl(REX_ARTICLE_ID, REX_CLANG_ID));\r\n$yform->setFormData($form_data);\r\n\r\n// action - showtext\r\nif (\'REX_VALUE[id=6]\' != \'\') {\r\n    $html = \'0\'; // plaintext\r\n    if (\'REX_VALUE[11]\' == 1) {\r\n        $html = \'1\'; // html\r\n    }\r\n\r\n    $e3 = \'\';\r\n    $e4 = \'\';\r\n    if ($html == \'0\') {\r\n        $e3 = \'<div class=\"alert alert-success\">\';\r\n        $e4 = \'</div>\';\r\n    }\r\n\r\n    $t = str_replace(\'<br />\', \'\', rex_yform::unhtmlentities(\'REX_VALUE[6]\'));\r\n    $yform->setActionField(\'showtext\', [$t, $e3, $e4, $html]);\r\n}\r\n\r\n$form_type = \'REX_VALUE[1]\';\r\n\r\n// action - email\r\nif ($form_type == \'1\' || $form_type == \'2\') {\r\n    $mail_from = (\'REX_VALUE[2]\' != \'\') ? \'REX_VALUE[2]\' : rex::getErrorEmail();\r\n    $mail_to = (\'REX_VALUE[12]\' != \'\') ? \'REX_VALUE[12]\' : rex::getErrorEmail();\r\n    $mail_subject = \'REX_VALUE[4]\';\r\n    $mail_body = str_replace(\'<br />\', \'\', rex_yform::unhtmlentities(\'REX_VALUE[5]\'));\r\n    $yform->setActionField(\'email\', [$mail_from, $mail_to, $mail_subject, $mail_body]);\r\n}\r\n\r\n// action - db\r\nif ($form_type == \'0\' || $form_type == \'2\') {\r\n    $yform->setObjectparams(\'main_table\', \'REX_VALUE[8]\');\r\n\r\n    if (\'REX_VALUE[10]\' != \'\') {\r\n        $yform->setObjectparams(\'getdata\', true);\r\n    }\r\n\r\n    $yform->setActionField(\'db\', [\'REX_VALUE[8]\', $yform->objparams[\'main_where\']]);\r\n}\r\n\r\necho $yform->getForm();\r\n','<?php\r\n\r\n/**\r\n * yform\r\n * @author jan.kristinus[at]redaxo[dot]org Jan Kristinus\r\n * @author <a href=\"http://www.yakamara.de\">www.yakamara.de</a>\r\n */\r\n\r\n// module:yform_basic_input\r\n// v1.1\r\n// --------------------------------------------------------------------------------\r\n\r\n// DEBUG SELECT\r\n////////////////////////////////////////////////////////////////////////////////\r\n$dbg_sel = new rex_select();\r\n$dbg_sel->setName(\'REX_INPUT_VALUE[7]\');\r\n$dbg_sel->setAttribute(\'class\', \'form-control\');\r\n$dbg_sel->addOption(\'inaktiv\',\'0\');\r\n$dbg_sel->addOption(\'aktiv\',\'1\');\r\n$dbg_sel->setSelected(\'REX_VALUE[7]\');\r\n$dbg_sel = $dbg_sel->get();\r\n\r\n\r\n// TABLE SELECT\r\n////////////////////////////////////////////////////////////////////////////////\r\n$gc = rex_sql::factory();\r\n$gc->setQuery(\'SHOW TABLES\');\r\n$tables = $gc->getArray();\r\n$tbl_sel = new rex_select();\r\n$tbl_sel->setName(\'REX_INPUT_VALUE[8]\');\r\n$tbl_sel->setAttribute(\'class\', \'form-control\');\r\n$tbl_sel->addOption(\'Keine Tabelle ausgewählt\', \'\');\r\nforeach ($tables as $key => $value) {\r\n  $tbl_sel->addOption(current($value), current($value));\r\n}\r\n$tbl_sel->setSelected(\'REX_VALUE[8]\');\r\n$tbl_sel = $tbl_sel->get();\r\n\r\n\r\n// PLACEHOLDERS\r\n////////////////////////////////////////////////////////////////////////////////\r\n$yform = new rex_yform;\r\n$form_data = \'REX_VALUE[3]\';\r\n$form_data = trim(str_replace(\'<br />\',\'\',rex_yform::unhtmlentities($form_data)));\r\n$yform->setFormData($form_data);\r\n$yform->setRedaxoVars(REX_ARTICLE_ID,REX_CLANG_ID);\r\n$placeholders = \'\';\r\nif(\'REX_VALUE[3]\'!=\'\') {\r\n  $ignores = array(\'html\',\'validate\',\'action\');\r\n  $placeholders .= \'\r\n        <div id=\"yform-js-formbuilder-placeholders\">\r\n            <h3>Platzhalter: <span>[<a href=\"#\" id=\"yform-js-formbuilder-placeholders-help-toggler\">?</a>]</span></h3>\'.PHP_EOL;\r\n  foreach($yform->objparams[\'form_elements\'] as $e) {\r\n    if(!in_array($e[0],$ignores) && isset($e[1])) {\r\n      $label = (isset($e[2]) && $e[2] != \'\') ? $e[2] . \': \' : \'\';\r\n      $placeholders .= \'<code>\' . $label . \'###\'.$e[1].\'###</code> \'.PHP_EOL;\r\n    }\r\n  }\r\n  $placeholders .= \'\r\n            <ul id=\"yform-js-formbuilder-placeholders-help\">\r\n                <li>Die Platzhalter ergeben sich aus den obenstehenden Felddefinitionen.</li>\r\n                <li>Per Klick können einzelne Platzhalter in den Mail-Body kopiert werden.</li>\r\n                <li>Aktualisierung der Platzhalter erfolgt über die Aktualisierung des Moduls.</li>\r\n            </ul>\r\n        </div>\'.PHP_EOL;\r\n}\r\n\r\n\r\n// OTHERS\r\n////////////////////////////////////////////////////////////////////////////////\r\n$row_pad = 1;\r\n\r\n$sel = \'REX_VALUE[1]\';\r\n$db_display   = ($sel==\'\' || $sel==1) ?\' style=\"display:none\"\':\'\';\r\n$mail_display = ($sel==\'\' || $sel==0) ?\' style=\"display:none\"\':\'\';\r\n\r\n?>\r\n\r\n<div id=\"yform-formbuilder\">\r\n  <fieldset class=\"form-horizontal\">\r\n    <legend>Formular</legend>\r\n    <div class=\"form-group\">\r\n      <label class=\"col-md-2 control-label text-left\">Debug Modus</label>\r\n      <div class=\"col-md-10\">\r\n        <div class=\"yform-select-style\">\r\n          <?= $dbg_sel; ?>\r\n        </div>\r\n      </div>\r\n    </div>\r\n    <div class=\"form-group\">\r\n      <label class=\"col-md-2 control-label\" for=\"yform-formbuilder-definition\">Felddefinitionen</label>\r\n      <div class=\"col-md-10\">\r\n        <textarea class=\"form-control\" style=\"font-family: monospace;\" id=\"yform-formbuilder-definition\" name=\"REX_INPUT_VALUE[3]\" rows=\"<?php echo (count(explode(\"\\r\",\'REX_VALUE[3]\'))+$row_pad);?>\">REX_VALUE[3]</textarea>\r\n      </div>\r\n    </div>\r\n    <div class=\"form-group\">\r\n      <label class=\"col-md-2 control-label\">Verfügbare Feldklassen</label>\r\n      <div class=\"col-md-10\">\r\n        <div id=\"yform-formbuilder-classes-showhelp\"><?= rex_yform::showHelp(); ?></div>\r\n      </div>\r\n    </div>\r\n    <div class=\"form-group\">\r\n      <label class=\"col-md-2 control-label\">Meldung bei erfolgreichen Versand</label>\r\n      <div class=\"col-md-10\">\r\n        <label class=\"radio-inline\">\r\n          <input type=\"radio\" name=\"REX_INPUT_VALUE[11]\" value=\"0\"<?php if(\"REX_VALUE[11]\" == \'0\') echo \' checked\'; ?> /> Plaintext\r\n        </label>\r\n        <label class=\"radio-inline\">\r\n          <input type=\"radio\" name=\"REX_INPUT_VALUE[11]\" value=\"1\"<?php if(\"REX_VALUE[11]\" == \'1\') echo \' checked\'; ?> /> HTML\r\n        </label>\r\n      </div>\r\n      <div class=\"col-md-offset-2 col-md-10\">\r\n        <textarea class=\"form-control\" name=\"REX_INPUT_VALUE[6]\" rows=\"<?php echo (count(explode(\"\\r\",\'REX_VALUE[6]\'))+$row_pad);?>\">REX_VALUE[6]</textarea>\r\n      </div>\r\n    </div>\r\n  </fieldset>\r\n\r\n  <fieldset class=\"form-horizontal\">\r\n    <legend>Vordefinierte Aktionen</legend>\r\n\r\n    <div class=\"form-group\">\r\n      <label class=\"col-md-2 control-label\">Bei Submit</label>\r\n      <div class=\"col-md-10\">\r\n        <div class=\"yform-select-style\">\r\n          <select class=\"form-control\" id=\"yform-js-formbuilder-action-select\" name=\"REX_INPUT_VALUE[1]\" size=\"1\">\r\n            <option value=\"\"<?php if(\"REX_VALUE[1]\" == \"\")  echo \" selected \"; ?>>Nichts machen (actions im Formular definieren)</option>\r\n            <option value=\"0\"<?php if(\"REX_VALUE[1]\" == \"0\") echo \" selected \"; ?>>Nur in Datenbank speichern</option>\r\n            <option value=\"1\"<?php if(\"REX_VALUE[1]\" == \"1\") echo \" selected \"; ?>>Nur E-Mail versenden</option>\r\n            <option value=\"2\"<?php if(\"REX_VALUE[1]\" == \"2\") echo \" selected \"; ?>>E-Mail versenden und in Datenbank speichern</option>\r\n          </select>\r\n        </div>\r\n      </div>\r\n    </div>\r\n  </fieldset>\r\n\r\n  <fieldset class=\"form-horizontal\" id=\"yform-js-formbuilder-mail-fieldset\"<?php echo $mail_display;?> >\r\n    <legend>E-Mail-Versand:</legend>\r\n\r\n    <div class=\"form-group\">\r\n      <label class=\"col-md-2 control-label\">Absender</label>\r\n      <div class=\"col-md-10\">\r\n        <input class=\"form-control\" type=\"text\" name=\"REX_INPUT_VALUE[2]\" value=\"REX_VALUE[2]\" />\r\n      </div>\r\n    </div>\r\n    <div class=\"form-group\">\r\n      <label class=\"col-md-2 control-label\">Empfänger</label>\r\n      <div class=\"col-md-10\">\r\n        <input class=\"form-control\" type=\"text\" name=\"REX_INPUT_VALUE[12]\" value=\"REX_VALUE[12]\" />\r\n      </div>\r\n    </div>\r\n    <div class=\"form-group\">\r\n      <label class=\"col-md-2 control-label\">Subject</label>\r\n      <div class=\"col-md-10\">\r\n        <input class=\"form-control\" type=\"text\" name=\"REX_INPUT_VALUE[4]\" value=\"REX_VALUE[4]\" />\r\n      </div>\r\n    </div>\r\n    <div class=\"form-group\">\r\n      <label class=\"col-md-2 control-label\">Mailbody</label>\r\n      <div class=\"col-md-10\">\r\n        <textarea class=\"form-control\" id=\"yform-js-formbuilder-mail-body\" name=\"REX_INPUT_VALUE[5]\" rows=\"<?php echo (count(explode(\"\\r\",\'REX_VALUE[5]\'))+$row_pad);?>\">REX_VALUE[5]</textarea>\r\n        <div class=\"help-block\">\r\n          <?php echo $placeholders;?>\r\n        </div>\r\n      </div>\r\n    </div>\r\n\r\n  </fieldset>\r\n\r\n  <fieldset class=\"form-horizontal\" id=\"yform-js-formbuilder-db-fieldset\"<?php echo $db_display;?> >\r\n    <legend>Datenbank Einstellungen</legend>\r\n\r\n    <div class=\"form-group\">\r\n      <label class=\"col-md-2 control-label\">Tabelle wählen <span>[<a href=\"#\" id=\"yform-js-formbuilder-db-help-toggler\">?</a>]</span></label>\r\n      <div class=\"col-md-10\">\r\n        <div class=\"yform-select-style\">\r\n          <?= $tbl_sel; ?>\r\n        </div>\r\n        <div class=\"help-block\">\r\n          <ul id=\"yform-js-formbuilder-db-help\">\r\n            <li>Hier werden die Daten des Formular hineingespeichert</li>\r\n          </ul>\r\n        </div>\r\n      </div>\r\n    </div>\r\n  </fieldset>\r\n\r\n</div>\r\n\r\n<p id=\"yform-formbuilder-info\"><?=  rex_addon::get(\'yform\')->getName() . \' \' . rex_addon::get(\'yform\')->getVersion() ?></p>\r\n\r\n<script type=\"text/javascript\">\r\n  <!--\r\n  (function($){\r\n\r\n    // AUTOGROW BY ROWS\r\n    $(\'#yform-formbuilder textarea\').keyup(function(){\r\n      var rows = $(this).val().split(/\\r?\\n|\\r/).length + <?php echo $row_pad;?>;\r\n      $(this).attr(\'rows\',rows);\r\n    });\r\n\r\n    // TOGGLERS\r\n    $(\'#yform-js-formbuilder-placeholders-help-toggler\').click(function(e){\r\n      e.preventDefault();\r\n      $(\'#yform-js-formbuilder-placeholders-help\').toggle(50);return false;\r\n    });\r\n    $(\'#yform-js-formbuilder-where-help-toggler\').click(function(e){\r\n      e.preventDefault();\r\n      $(\'#yform-js-formbuilder-where-help\').toggle(50);return false;\r\n    });\r\n    $(\'#yform-js-formbuilder-db-help-toggler\').click(function(e){\r\n      e.preventDefault();\r\n      $(\'#yform-js-formbuilder-db-help\').toggle(50);return false;\r\n    });\r\n\r\n\r\n    // INSERT PLACEHOLDERS\r\n    $(\'#yform-js-formbuilder-placeholders code\').click(function(){\r\n      newval = $(\'#yform-js-formbuilder-mail-body\').val()+\' \'+$(this).html();\r\n      $(\'#yform-js-formbuilder-mail-body\').val(newval);\r\n      $(this).addClass(\'text-muted\');\r\n    });\r\n\r\n    // TOGGLE MAIL/DB PANELS\r\n    $(\'#yform-js-formbuilder-action-select\').change(function(){\r\n      switch($(this).val()){\r\n        case \'\':\r\n          $(\'#yform-js-formbuilder-db-fieldset\').hide(0);\r\n          $(\'#yform-js-formbuilder-mail-fieldset\').hide(0);\r\n          break;\r\n        case \'1\':\r\n          $(\'#yform-js-formbuilder-db-fieldset\').hide(0);\r\n          $(\'#yform-js-formbuilder-mail-fieldset\').show(0);\r\n          break;\r\n        case \'0\':\r\n          $(\'#yform-js-formbuilder-db-fieldset\').show(0);\r\n          $(\'#yform-js-formbuilder-mail-fieldset\').hide(0);\r\n          break;\r\n        case \'2\':\r\n        case \'3\':\r\n          $(\'#yform-js-formbuilder-db-fieldset\').show(0);\r\n          $(\'#yform-js-formbuilder-mail-fieldset\').show(0);\r\n          break;\r\n      }\r\n    });\r\n\r\n  })(jQuery)\r\n  //-->\r\n</script>\r\n','0000-00-00 00:00:00','','2021-03-03 14:57:47','Thomas',NULL,0),
  (2,'text_markdown','Textabschnitt','<?php\r\n  $firstStage = str_replace(\"\\n\",\'<br>\',markitup::parseOutput (\'markdown\', \'REX_VALUE[id=1 output=\"html\"]\'));\r\n  echo str_replace(\'<br><\',\'<\',$firstStage);\r\n?>','<fieldset class=\"form-horizontal\">\r\n  <div class=\"form-group\">\r\n    <label class=\"col-sm-2 control-label\" for=\"value-1\">VALUE 1</label>\r\n    <div class=\"col-sm-10\">\r\n      <textarea class=\"form-control markitupEditor-markdown_full\" id=\"value-1\" name=\"REX_INPUT_VALUE[1]\">REX_VALUE[1]</textarea>\r\n    </div>\r\n  </div>\r\n</fieldset>','2020-05-22 16:37:28','Thomas','2021-03-03 14:57:47','Thomas',NULL,0),
  (3,'tth_view_list','Listenausgabe','<?php\r\n// - just hard code all you need for the list\r\n// - use rex_pager\r\n\r\n$sql = rex_sql::factory();\r\n\r\n// !!! set offset and limit by pager (depends on usage of pager, if i had an own pager i would store the last if of the last query??)\r\n\r\n// ! you must not rely on order in DB even if currently ordered alphabetically in table\r\n// ! the idea is that there never be more than about 3000 entries (because older versions of begriff (modification history) are seldom used in overview\r\n$query = \'SELECT begriff FROM tth_wortliste WHERE 1 ORDER BY begriff\';\r\n// $query = \'SELECT id,begriff,grobgliederung FROM tth_wortliste WHERE grobgliederung LIKE \"%;%\"\';\r\n$rows = $sql->getArray($query);\r\n$i = 1;\r\n$count = count($rows);\r\nforeach($rows as $r) {\r\n	echo $r[\'begriff\'];\r\n	if ($i < $count) echo \', \';\r\n	$i++;\r\n}\r\n?>.','Keine Einstellungen. Einfach Hinzufügen','2020-05-23 19:21:51','Thomas','2020-05-23 20:21:40','Thomas',NULL,0),
  (4,'tth_view_list_letter','Liste nach Anfangsbuchstaben','<p>\r\n<a id=\"marker-letters\"></a>\r\n<?php\r\n// - just hard code all you need for the list\r\n// - even target page id\r\n$targetArticleId = 6;\r\n// !!! make things dynamic later (e.g. via rex_config)\r\n\r\nfor($i = 0; $i <= 25; $i++ ) {\r\n	$letter = chr($i+65);\r\n	if (23 !== $i && 24 !== $i) {\r\n		echo \'<a href=\"\'.rex_getUrl(\'\',\'\',array( \'startletter\' => $letter)).\'#marker-letters\" class=\"btn btn-primary\">\'.$letter.\'</a> \';\r\n	}\r\n}\r\n\r\n$startLetter = rex_request(\'startletter\');\r\nif ($startLetter) {\r\n	$startLetter = strtoupper($startLetter);\r\n	// dump($startLetter);\r\n	$sql = rex_sql::factory();\r\n\r\n	$query = \'SELECT id, begriff FROM tth_wortliste WHERE begriff LIKE \"\'.$startLetter.\'%\" ORDER BY begriff ASC\';\r\n	// $query = \'SELECT id,begriff,grobgliederung FROM tth_wortliste WHERE grobgliederung LIKE \"%;%\"\';\r\n	$rows = $sql->getArray($query);\r\n	$i = 1;\r\n	$count = count($rows);\r\n	foreach($rows as $r) {\r\n		echo \'<a href=\"\'.rex_getUrl($targetArticleId,\'\',array( \'begriff_id\' => $r[\'id\'])).\'\">\';\r\n		echo $r[\'begriff\'];\r\n		echo \'</a>\';\r\n		if ($i < $count) echo \', \';\r\n		$i++;\r\n	}\r\n}\r\n?>\r\n</p>','','2020-05-23 20:27:10','Thomas','2020-06-13 17:45:36','Thomas',NULL,0),
  (5,'tth_view_item','Detailansicht','<!-- you will need to use bootstrap rows/cols -->\r\n<?php\r\n	if (rex::isBackend()) {\r\n		// !!! make general function for \"backend-page link\"\r\n		?>\r\n		<p>Ergebnisse nur im Frontend.</p>\r\n		<p>Bei fehlender ID des Begriffs wird eine Warnung ausgegeben.</p>\r\n		<p>Die Ziel-Seite der globalen Suche sollte dieses Modul enthalten. Da bei Suchmuster eine Auswahlliste erzeugt wird, wenn das Suchmuster auf mehrere Begriffe passt.</p>\r\n		<p>Artikel für Quellen: <strong>REX_LINK[id=1 output=name]</strong> (ID: REX_LINK[id=1 output=id])</p>\r\n		<p>Artikel für Schlagwörter:  <strong>REX_LINK[id=2 output=name]</strong> (ID: REX_LINK[id=2 output=id])</p>\r\n		<?php\r\n	}\r\n	else {\r\n		define(\"DETAIL_VIEW\", \"detail\");\r\n\r\n		?>\r\n		<div class=\"row detailed-view\">\r\n		<div class=\"col\">\r\n		<?php\r\n\r\n		$sql = rex_sql::factory();\r\n		$id = 0;\r\n		// first check search results,\r\n		// maybe an $id can be derived (only one word found)\r\n		$wSearch = rex_escape(rex_request(\'wordlistsearch\',\'string\'));\r\n		if ($wSearch) {\r\n			$searchPattern = rex_escape($wSearch);\r\n			if ($searchPattern) {\r\n				// ! change pattern to always have PART of word when no *\r\n				if (false === strpos($searchPattern,\'*\')) {\r\n					$searchPattern = \'*\'.$searchPattern.\'*\';\r\n				}\r\n				\r\n				$searchPattern = \r\n					str_replace(\"\'\",\"\",\r\n					str_replace(\"`\",\"\",\r\n					str_replace(\'\"\',\'\',\r\n					str_replace(\"\\n\",\'\',\r\n					str_replace(\';\',\'\',\r\n					$searchPattern\r\n				)))));\r\n\r\n				$query = \"SELECT id,begriff from tth_wortliste WHERE begriff LIKE \".str_replace(\'*\',\'%\',$sql->escape($searchPattern));\r\n				\r\n				$rows = $sql->getArray($query);\r\n				\r\n				$searchResultList = \'\';\r\n				if (count($rows)) {\r\n					if(count($rows) === 1) {\r\n						$id = $rows[0][\'id\'];\r\n						$searchResultList = \'found exactly one begriff: \'.$rows[0][\'begriff\'];\r\n					}\r\n					$tm = new \\kwd\\tth\\TableManager();\r\n					$searchResultList = $tm->makeLinkList($rows, \'begriff_id\', \'begriff\', $detailsArticleId);\r\n				}\r\n			}\r\n		}\r\n\r\n		$tm = new \\kwd\\tth\\TableManager();\r\n		if (!$id) $id = rex_request(\'begriff_id\',\'int\');\r\n		if ($id) {\r\n			$sourcesArticleId = \'REX_LINK[id=1 output=id]\';\r\n			$tagsArticleId = \'REX_LINK[id=2 output=id]\';\r\n			if (!$sourcesArticleId) $sourcesArticleId = \'REX_ARTICLE_ID\';\r\n			$tableEntities = \'tth_wortliste\';\r\n			$tableAuthors = \'tth_autoren\';\r\n			$tableStati = \'tth_begriffsstati\';\r\n			$tableLanguage = \'tth_sprachen\';\r\n			$tableRegions = \'tth_regionen\';\r\n			$tableStyles = \'tth_sprachstile\';\r\n			$tableTags = \'tth_tags\';\r\n			$tableSources = \'tth_quellen\'; // needed for resolving names in $tableReferences\r\n			// $tableRelationSources = \'tth_begriff_quellen\';\r\n			$tableReferences = \'tth_quellenangaben\';\r\n			$tableRelationTags = \'tth_begriff_tags\';\r\n\r\n			// $sql = rex_sql::factory();\r\n			// !!! test relations with yform functions, so that no own queries needed\r\n			\r\n			// ! synonyms found can not be generated by $tm->getInnerRelationLinkList() because we want to find\r\n			//   all enities which point to the current one\r\n			$synonymsQuery = \"SELECT t1.id,t1.begriff from $tableEntities t1 WHERE t1.benutze=$id\";\r\n			$synonyms = $sql->getArray($synonymsQuery);\r\n			\r\n			// references list\r\n			$query = \"SELECT r.id, r.quelle_id, r.seitenzahl, r.bevorzugt, s.kurz \"; //s.kurz\r\n			$query.= \"FROM $tableReferences r \";\r\n			$query.= \"JOIN $tableSources s ON r.quelle_id = s.id \";\r\n			// $query.= \"JOIN $tableSources ON $tableSources.id = $tableRelationSources.quelle_id \";\r\n			$query.= \"WHERE r.begriff_id=$id ORDER BY r.id ASC\";\r\n			$sourcesArray = $sql->getArray($query);\r\n			\r\n			// tags\r\n			// !!! could make \"getOuterRelation in table manager\r\n			$tagsQuery = \"SELECT r.tag_id, r.begriff_id, s.name \";\r\n			$tagsQuery.= \"FROM $tableRelationTags r \";\r\n			$tagsQuery.= \"JOIN $tableTags s ON r.tag_id = s.id \";\r\n			$tagsQuery.= \"WHERE r.begriff_id=$id ORDER BY s.name ASC\";\r\n			$tags = $sql->getArray($tagsQuery);\r\n			\r\n			// ! b is first alias for $tableEntities, b2 is the second for benutze\r\n			$query = \"SELECT b.begriff,b.id,$tableAuthors.gnd,b.quelle_seite,b.code,b.definition,b.bild,b.begriffsstatus_id,$tableStati.status,b.notes,b.benutze,b.kategorie,b.veroeffentlichen,b.bearbeiten,\";\r\n			$query .= \"b2.begriff AS benutze_begriff,CONCAT($tableAuthors.vorname, \' \', $tableAuthors.name) AS autor,\";\r\n			$query .= \"$tableLanguage.sprache AS sprache,\";\r\n			$query .= \"$tableRegions.region AS region, \";\r\n			$query .= \"$tableStyles.stil AS sprachstil \";\r\n			$query .= \"FROM $tableEntities b \";\r\n			$query .= \"LEFT JOIN $tableAuthors ON b.autor_id = $tableAuthors.id \";\r\n			$query .= \"LEFT JOIN $tableStati ON b.begriffsstatus_id = $tableStati.id \";\r\n			$query .= \"LEFT JOIN $tableLanguage ON b.sprache_id = $tableLanguage.id \";\r\n			$query .= \"LEFT JOIN $tableRegions ON b.region_id = $tableRegions.id \";\r\n			$query .= \"LEFT JOIN $tableStyles ON b.sprachstil_id = $tableStyles.id \";\r\n			$query .= \"LEFT JOIN $tableEntities b2 ON b2.id = b.benutze WHERE b.id=$id\";\r\n			\r\n			$rows = $sql->getArray($query);\r\n			if ($rows && count($rows)) {\r\n				$r = $rows[0]; // because selected by ID, there can only be 1 row\r\n					\r\n				$detailView = rex_escape(rex_request(\'view\',\'string\'));\r\n				if (DETAIL_VIEW === $detailView) {\r\n				\r\n					$html = \"<h2>${r[\"begriff\"]}</h2>\\n\";\r\n					\r\n					// !!! type check!\r\n					if ($r[\'begriffsstatus_id\'] == 8) {\r\n						echo rex_view::warning(\'Dies ist eine \"Facette\". Es ist somit ein Oberbegriff der höchsten Hierarchieebene und kann nicht als normale Entität behandelt werden.\');\r\n					}\r\n\r\n					$html .= \'<table class=\"table table-responsive\">\';\r\n					\r\n					// make header line of table\r\n					// $html .= \'<thead><tr><th>Feld</th><th>Inhalt</th></thead>\';\r\n					\r\n					// $html .= $tm->makeRow(\'ID\', $r[\'id\']);\r\n					$html .= $tm->makeRow(\'Definition\', $r[\'definition\']);\r\n					\r\n					$html .= $tm->makeRow(\'Sprache\', $r[\'sprache\']);\r\n					$html .= $tm->makeRow(\'Sprachstil\', $r[\'sprachstil\']);\r\n					$html .= $tm->makeRow(\'Region\', ($r[\'region\']) ? $r[\'region\'] : \"\");\r\n					$html .= $tm->makeRow(\'Begriffcode\', $r[\'code\']);\r\n					$html .= $tm->makeRow(\'Begriffs-Status\',$r[\'status\']);\r\n					// ! redaxo file list is *comma* separated\r\n					if ($r[\'bild\']) {\r\n						// ! separator \',\' is determined by redaxo\r\n						$images = explode(\',\',$r[\'bild\']);\r\n						$imgHTML = \'\';\r\n						foreach ($images as $img) {\r\n							$imgHTML .= \'<img src=\"index.php?rex_media_type=tth_horizontal_list&rex_media_file=\'.$img.\'\">\';\r\n						}\r\n						$html .= $tm->makeRow(\'Bilder\',$imgHTML);\r\n					}\r\n					\r\n					$html .= $tm->makeRow(\r\n						\'Grobgliederung\',\r\n						$tm->getInnerRelationLinkList($sql, \'structuring\', $id)\r\n					);\r\n					$html .= $tm->makeRow(\r\n						\'Begriffe, bei denen Grobgliederung auf diesen Begriff verweist\',\r\n						$tm->getInnerReverseRelationLinkList($sql, \'structuring\', $id)\r\n					);\r\n					\r\n					\r\n					// !!! need method for this code synonyms + tags\r\n					$tagList = \'\';\r\n					foreach($tags as $s) {\r\n						$tagList .= $tm->getLink(\'tag_id\', $s[\'tag_id\'],$s[\'name\'], $tagsArticleId).\', \';\r\n					}\r\n					// !!! use small def from Bootstrap\r\n					$html .= $tm->makeRow(\'Schlagwörter\',$tagList.\'<br><small></small>\');\r\n					\r\n					\r\n					// ! first link\r\n					$html .= $tm->makeRow(\'Synonym von (Benutze)\',$tm->getLink(\'begriff_id\', $r[\'benutze\'], $r[\'benutze_begriff\']).\'<br><small>dies ist der Desriptor und damit Name der <em>Äquivalenzklasse</em></small>\');\r\n					\r\n					$syns = \'\';\r\n					foreach($synonyms as $s) {\r\n						$syns .= $tm->getLink(\'begriff_id\', $s[\'id\'],$s[\'begriff\']).\', \';\r\n					}\r\n					\r\n					// !!! use small def from Bootstrap\r\n					$html .= $tm->makeRow(\'Deskriptor von (Benutzt für)\',$syns.\'<br><small>diese sind zusammen mit dem Begriff \"\'.$r[\'begriff\'].\'\" selbst die <em>Äquivalenzklasse</em></small>\');\r\n					\r\n					// tags\r\n					// $html .= $tm->makeRow(\r\n						// 	\'Schlagwörter\',\r\n						// 	// !!! wrong: is not *inner* relation\r\n						// 	$tm->getInnerRelationLinkList($sql, \'tags\', $id)\r\n						// );\r\n						\r\n						$html .= $tm->makeRow(\r\n							\'Oberbegriffe\',\r\n							$tm->getInnerRelationLinkList($sql, \'supers\', $id)\r\n						);\r\n						// ! must be \"unterbegriffe\" reverse to show generated \"oberbegriffe\"\r\n						$html .= $tm->makeRow(\r\n							\'\',\r\n							$tm->getInnerReverseRelationLinkList($sql, \'subs\', $id)\r\n						);\r\n						\r\n						$html .= $tm->makeRow(\r\n							\'Unterbegriffe\',\r\n							$tm->getInnerRelationLinkList($sql, \'subs\', $id)\r\n						);\r\n						// ! must be \"oberbegriffe\" reverse to show generated \"unterbegriffe\"\r\n						$html .= $tm->makeRow(\r\n							\'\',\r\n							$tm->getInnerReverseRelationLinkList($sql, \'supers\', $id)\r\n						);\r\n							\r\n						$html .= $tm->makeRow(\r\n							\'Äquivalente Begriffe\',\r\n							$tm->getInnerRelationLinkList($sql, \'equivalents\', $id)\r\n						);\r\n						\r\n						$html .= $tm->makeRow(\r\n							\'Verwandte Begriffe\',\r\n							$tm->getInnerRelationLinkList($sql, \'relatives\', $id)\r\n						);\r\n						\r\n						if ($r[\'quelle_seite\']) $html .= $tm->makeRow(\'Seite in Quelle\',$r[\'quelle_seite\']);\r\n						\r\n						// $html .= $tm->makeRow(\'Quellen\',$tm->makeLinkList($sourcesArray, \'quelle_id\', \'kurz\', $sourcesArticleId));\r\n						\r\n						$html .= $tm->makeRow(\'Scoped Notes\',$r[\'notes\']);\r\n						$html .= $tm->makeRow(\'Kategorie\',$tm->checkTruthyWord($r[\'kategorie\']));\r\n						$html .= $tm->makeRow(\'Veröffentlichen?\',$tm->checkTruthyWord($r[\'veroeffentlichen\']));\r\n						$html .= $tm->makeRow(\'Noch bearbeiten\',$tm->checkTruthyWord($r[\'bearbeiten\']));\r\n						\r\n						$authorText = \'\';\r\n						// ! the `if` is important because the SQL may still returen the first entry of tth_autoren for some reason when autor_id=\'\'  ! whole data set not returned when no author; need 0 clause in inner join\r\n						if ($r[\'autor\']) { // is a generated value; and is NULL when not set\r\n							$authorText .= $r[\'autor\'];\r\n							// !!! provide link for GND (see code in details of \"Quelle\")\r\n							if (trim($r[\'gnd\'])) $authorText .= \' (GND: \'.$r[\'gnd\'].\')\';\r\n						}\r\n						$html .= $tm->makeRow(\'Autor\',$authorText);\r\n						$html .= \'</table>\';\r\n						echo $html;\r\n					}\r\n					else {\r\n						$html = \'<table class=\"table table-responsive\">\';\r\n\r\n						\r\n\r\n						// row 1\r\n						$html .= \'<tr>\';\r\n						$html .= \'<td>Facette: ?</td>\';\r\n						$html .= \'<td>\';\r\n						$html .= $tm->getInnerRelationLinkList($sql, \'supers\', $id);\r\n						// ! must be \"unterbegriffe\" reverse to show generated \"oberbegriffe\"\r\n						$html .= \'<br>\'.$tm->getInnerReverseRelationLinkList($sql, \'subs\', $id);\r\n						$html .= \'</td>\';\r\n						$html .= \'<td></td>\';\r\n						$html .= \'</tr>\';\r\n\r\n						// row 2\r\n						$html .= \'<tr>\';\r\n						$html .= \'<td>Deskriptor:<br>Synonyme<br>:\';\r\n						$html .= $tm->getInnerRelationLinkList($sql, \'equivalents\', $id);\r\n						// !!! cool would be \'br\' only when needed -> make sub function for all reverse addition\r\n						$html .= \'<br>\'.$tm->getInnerReverseRelationLinkList($sql, \'equivalents\', $id);\r\n						$html .\'</td>\';\r\n						$html .= \'<td class=\"entity-center\"><h2>\'.$r[\"begriff\"].\'</h2>\';\r\n						$html .= \'<p id=\"entity-definition\" class=\"entity-definition\">\'.$r[\'definition\'].\'</p></td>\';\r\n						$html .= \'<td>Verwandte<br>\';\r\n						$html .= $tm->getInnerRelationLinkList($sql, \'relatives\', $id);\r\n						$html .= \'<br>\'.$tm->getInnerReverseRelationLinkList($sql, \'relatives\', $id);\r\n						$html .= \'</td>\';\r\n						$html .= \'</tr>\';\r\n\r\n						// row 3\r\n						$html .= \'<tr>\';\r\n						$html .= \'<td></td>\';\r\n						$html .= \'<td>\';\r\n						$html .= $tm->getInnerRelationLinkList($sql, \'subs\', $id);\r\n						// ! must be \"unterbegriffe\" reverse to show generated \"oberbegriffe\"\r\n						$html .= \'<br>\'.$tm->getInnerReverseRelationLinkList($sql, \'supers\', $id);\r\n						$html .= \'</td>\';\r\n						$html .= \'<td></td>\';\r\n						$html .= \'</tr>\';\r\n\r\n						$html .= \'</table>\';\r\n						echo $html;\r\n\r\n						$detailUrl = rex_getUrl(\'\',\'\', array (\'begriff_id\' => $id, \'view\' => \'detail\'));\r\n						?>\r\n						<p><a href=\"<?=$detailUrl?>\">Details und Zusatzinformationen anzeigen.</a></p>\r\n						<?php\r\n					}\r\n						\r\n					// dump($rows[0]);\r\n					// begin new table for Sources-Entries:\r\n						$html = \'<hr><table class=\"table table-responsive\">\';\r\n						// make header line of table\r\n						$html .= \"<thead><tr><th>#</th><th>Quelle</th><th>Seitenzahl</th><th>Bevorzugt?</th></thead>\\n\";\r\n						$i = 1;\r\n						foreach($sourcesArray as $s) {\r\n							$html .= \"<tr>\\n\";\r\n							$html .= \"<td>$i</td> \";\r\n							$html .= \"<td>\".$tm->getLink(\'quelle_id\',$s[\'quelle_id\'], \'<div class=\"author-name\">\'.$s[\'kurz\'].\'</div>\', $sourcesArticleId).\"</td> \";\r\n							// check for any truthy value\r\n							$html .= \"<td>\".($s[\'seitenzahl\'] ? $s[\'seitenzahl\'] : \'\').\"</td> \";\r\n							// check for any truthy value\r\n							$html .= \"<td>\".($s[\'bevorzugt\'] ? \'bevorzugt\' : \'\').\"</td> \";\r\n							// $html .= \"<td>${s[\'bevorzugt\']}</td> \";\r\n							$html .= \"</tr>\\n\";\r\n							$i++;\r\n						}\r\n						echo $html.\'</table>\';\r\n					\r\n						// write a string with begriff\r\n						echo \'{{{\'.$r[\'begriff\'].\'}}}\';\r\n						// rex_config::set(\'tth\',\'current_entity\',$r[\'begriff\']);\r\n					}\r\n					else {\r\n						echo rex_view::warning(\'Eintrag für ID = \'.$id.\' nicht gefunden.\');\r\n					}\r\n\r\n				}\r\n\r\n				// !!! check: remove this if when hierarchy is always on other page \r\n				else if (!rex_request(\'facette_id\')) {\r\n					if ($wSearch) {\r\n						echo \'<div class=\"card\"><div class=\"card-body\"><h5 class=\"card-title\">\'.($searchResultList ? \'\' : \'<strong>Keine</strong> \').\'Ergebnisse für \"\'.$wSearch.\'\":</h5><p>\'.$searchResultList.\'</p></div></div>\';\r\n					}\r\n					else if (!$id) {\r\n						// !!! make text editable in module input\r\n						echo rex_view::warning(\'Kein Begriff ausgewählt.\');\r\n					}\r\n				}\r\n			?>\r\n	</div>\r\n</div>\r\n				<?php\r\n			}\r\n			?>\r\n','<p>Artikel, der Detailansicht der <em>Literatur-Quellen</em> enthält:</p>\r\nREX_LINK[id=1 widget=1]\r\n<p>Artikel, der Übersicht der <em>Schlagwörter</em> enthält:</p>\r\nREX_LINK[id=2 widget=1]\r\n','2020-05-25 10:51:48','Thomas','2021-03-03 14:58:22','Thomas',NULL,0),
  (6,'tth_view_source','Quellen-Detailansicht','<?php\r\n	$tm = new \\kwd\\tth\\TableManager();\r\n\r\n$quelleId = rex_request(\'quelle_id\',\'int\'); // the param \'int\' should prevent from dangerous inputs\r\n// is_numeric is enough sanitize if we just need an id\r\nif ($quelleId && is_numeric($quelleId)) {\r\n	echo \'Zeige Quelle id \'.$quelleId;\r\n	$sql = rex_sql::factory();\r\n	// $rows = $sql->getArray(\"SELECT q.*,a.* FROM tth_quellen q LEFT JOIN tth_autoren a ON a.id = q.autor_id WHERE q.id = $quelleId\");\r\n	$rows = $sql->getArray(\"SELECT q.* FROM tth_quellen q WHERE q.id = $quelleId\");\r\n	// don\'t know how to combine the queries\r\n	// select all authors for this source, similar to code for relation in begriffe\r\n	$query = \"SELECT a.* \";\r\n	$query.= \"FROM tth_autoren a \";\r\n	$query.= \"JOIN tth_quellen_autoren g1 ON a.id = g1.autor_id \";\r\n	$query.= \"JOIN tth_quellen q ON q.id = g1.quelle_id \";\r\n	$query.= \"WHERE q.id=$quelleId \";\r\n\r\n	$authors = $sql->getArray($query);\r\n\r\n	if (count($rows)) {\r\n		$r = $rows[0];\r\n		echo \"<h2>${r[\'kurz\']}</h2>\";\r\n		?>\r\n		<table class=\"table table-responsive\">\r\n		<?php\r\n		$html = \'\';\r\n		$html .= $tm->makeRow(\'Titel\',$r[\'titel\']);\r\n		$html .= $tm->makeRow(\'Jahr\',$r[\'jahr\']);\r\n		$html .= $tm->makeRow(\'ISBN\',$r[\'isbn\']);\r\n		$html .= $tm->makeRow(\'Kurzform\',$r[\'kurz\']);\r\n		// !!! make link to details for author table (or general link to table with all authors, still not many)\r\n		$authorsHtml = \'\';\r\n		foreach($authors as $a) {\r\n			$authorsHtml .= \"${a[\'vorname\']} ${a[\'name\']}\";\r\n			if ($a[\'gnd\']) {\r\n				// !!! make as config var or sprog/string replacement\r\n				$authorsHtml .= \" (GND: <a href=\'https://portal.dnb.de/opac.htm?query=${a[\'gnd\']}\'>${a[\'gnd\']}</a>)\";\r\n			}\r\n			$authorsHtml .= \", \";\r\n		}\r\n		$html .= $tm->makeRow(\'Autoren\',$authorsHtml.\'<small>alte Autor-IDs zum Abgleich: \'.$r[\'autor_id\'].\'</small>\');\r\n\r\n		echo $html;\r\n		?>\r\n		</table>\r\n		<?php\r\n	}\r\n	else {\r\n		// !!! again use module vars for text template\r\n		echo rex_view::warning(\'Kein Datensatz gefunden für ID=\'.$quelleId);\r\n	}\r\n}\r\n?>','','2020-05-26 11:03:42','Thomas','2020-06-28 19:47:18','Thomas',NULL,0),
  (7,'tth_view_list_param','Liste nach Parameter (nur Begriffe)','<?php\r\n// - put all tth_view_list* together for less modules\r\n// - set params ins module instead for start view\r\n// - control output by params from url\r\n\r\nif (!function_exists(\'printCard\')) {\r\n	function printCard($html) {\r\n		if (trim($html)) {\r\n			$heading = substr($html, 0, strpos($html, \':\'));\r\n			$list = substr($html, strpos($html, \':\')+1);\r\n			// count entries because not returned by table_manager:\r\n			$count = substr_count($list, \'<a \');\r\n			?>\r\n			<div class=\"card\">\r\n				<div class=\"card-header\"><?=$heading?> (<?=$count?>):</div>\r\n				<div class=\"card-body\"><?=$list?></div>\r\n			</div>\r\n			<?php\r\n		}\r\n	}\r\n}\r\n\r\nif (!function_exists(\'printFilteredLinks\')) {\r\n	function printFilteredLinks(&$sql,&$tm, $table,$idName,$fieldName) {\r\n		return $tm->getFilteredEntities($sql, $table, $idName, $fieldName,rex_escape(rex_request($idName)), \'REX_LINK[id=1 output=id]\');\r\n	}\r\n}\r\n\r\n$detailsArticleId = \'REX_LINK[id=1 output=id]\';\r\n\r\n$tm = new \\kwd\\tth\\TableManager();\r\n\r\n// !!! check why search form evaluation HERE\r\n// !!! probably you should use PHP classes/methods to evaluate it(?)\r\n$search = rex_request(\'FORM\');\r\nif ($search) {\r\n	$searchPattern = $search[\'formular\'][0];\r\n	if ($searchPattern) {\r\n\r\n		?><p>Suchmuster: <strong><?=$searchPattern?></strong>\r\n		<?php		\r\n		// !!! sanitize (e.g. only letters, underscore, space and *)\r\n\r\n		// ! change pattern to always have PART of word when no *\r\n		if (false === strpos($searchPattern,\'*\')) {\r\n			$searchPattern = \'*\'.$searchPattern.\'*\';\r\n		}\r\n		\r\n		// !!! set article id by module param\r\n		$sql = rex_sql::factory();\r\n		$query = \"SELECT id,begriff from tth_wortliste WHERE begriff LIKE \'\".str_replace(\'*\',\'%\',$searchPattern).\"\'\";\r\n		?>\r\n		<div class=\"link-list\"><?=$tm->makeLinkList($sql->getArray($query), \'begriff_id\', \'begriff\', $detailsArticleId);?></div>\r\n		<?php\r\n	}\r\n}\r\nelse {\r\n	// !!! do rex_escape\r\n	// !!! make in function/class? request id, name of field for name, table name, headline, target page id\r\n	// $lang = rex_request(\'sprache_id\');\r\n	// $region = rex_request(\'region_id\');\r\n	// $style = rex_request(\'sprachstil_id\');\r\n	$source = rex_request(\'quelle_id\');\r\n	// $status = rex_request(\'begriffsstatus_id\');\r\n\r\n	$sql = rex_sql::factory();\r\n\r\n	printCard(printFilteredLinks($sql, $tm, \'tth_sprachen\',\'sprache_id\', \'sprache\'));\r\n	printCard(printFilteredLinks($sql, $tm, \'tth_begriffsstati\', \'begriffsstatus_id\', \'status\'));\r\n	printCard(printFilteredLinks($sql, $tm, \'tth_regionen\', \'region_id\', \'region\'));\r\n	printCard(printFilteredLinks($sql, $tm, \'tth_sprachstile\', \'sprachstil_id\', \'stil\'));\r\n\r\n	if ($source || 0 === $source || \'0\' === $source) {\r\n		$sql = rex_sql::factory();\r\n\r\n		// !!! you\'ll need to find another way for \"not set\"\r\n		//     - need extra treatment for $source === 0\r\n\r\n		$tableEntities = \'tth_wortliste\';\r\n		$tableRelationSources = \'tth_begriff_quellen\';\r\n		$tableSources = \'tth_quellen\';\r\n\r\n		if ($source) {\r\n\r\n			$query = \"SELECT $tableEntities.id,$tableEntities.begriff\r\n			FROM $tableRelationSources\r\n			INNER JOIN $tableEntities\r\n			ON $tableRelationSources.begriff_id = $tableEntities.id\r\n			INNER JOIN $tableSources\r\n			ON $tableRelationSources.quelle_id = $tableSources.id\r\n			WHERE $tableSources.id = $source\";\r\n		}\r\n		else {\r\n			$query = \"SELECT $tableEntities.id,$tableEntities.begriff\r\n			FROM   $tableEntities\r\n			WHERE  NOT EXISTS\r\n			  (SELECT *\r\n			   FROM   $tableRelationSources\r\n			   WHERE  $tableRelationSources.begriff_id = $tableEntities.id)\";\r\n		}\r\n			\r\n		$rows = $sql->getArray($query);\r\n		\r\n		// !!! again: provide texts by input value or by SPROG replacement\r\n		?><p><strong>Begriffe nach Quelle (<?=($source ? \'ID=\'.$source : \'nicht gesetzt\')?>, <?=count($rows)?> Einträge):</strong> <?=$tm->makeLinkList($rows, \'begriff_id\', \'begriff\', \'REX_LINK[id=1 output=id]\')?></p>\r\n		<?php	\r\n	}\r\n	\r\n	echo \'<hr>\';\r\n\r\n	// !!! output only depending on param in module input because sometimes not wanted\r\n	$sql = rex_sql::factory();\r\n\r\n	// !!! make functions for the array selects\r\n	$languages = $sql->getArray(\"SELECT id,sprache FROM tth_sprachen WHERE 1\");\r\n	// id = 0 automatically causes SQL to find unset entries\r\n	array_push($languages, array( \'id\' => 0, \'sprache\' => \'nicht gesetzt\'));\r\n	?>\r\n	<p><strong>Sprache:</strong> <?=$tm->makeLinkList($languages, \'sprache_id\', \'sprache\')?></p>\r\n	<?php\r\n\r\n	$regions = $sql->getArray(\"SELECT id,region FROM tth_regionen WHERE 1\");\r\n	array_push($regions, array( \'id\' => 0, \'region\' => \'nicht gesetzt\'));\r\n	?>\r\n	<p><strong>Region:</strong> <?=$tm->makeLinkList($regions, \'region_id\', \'region\')?></p>\r\n	<?php\r\n\r\n	$styles = $sql->getArray(\"SELECT id,stil FROM tth_sprachstile WHERE 1\");\r\n	array_push($styles, array( \'id\' => 0, \'stil\' => \'nicht gesetzt\'));\r\n	?>\r\n	<p><strong>Sprachstil:</strong> <?=$tm->makeLinkList($styles, \'sprachstil_id\', \'stil\')?></p>\r\n	<?php\r\n\r\n	$stati = $sql->getArray(\"SELECT id,`status` FROM tth_begriffsstati WHERE 1 ORDER BY id ASC\");\r\n	array_push($stati, array( \'id\' => 0, \'status\' => \'nicht gesetzt\'));\r\n	?>\r\n	<p><strong>Begriffsstatus:</strong> <?=$tm->makeLinkList($stati, \'begriffsstatus_id\', \'status\')?></p>\r\n	<?php\r\n\r\n	$quellen = $sql->getArray(\"SELECT id,kurz FROM tth_quellen WHERE 1\");\r\n	// ! you\'ll need to find another way for \"not set\"\r\n	array_push($quellen, array( \'id\' => 0, \'kurz\' => \'nicht gesetzt\'));\r\n	?>\r\n	<p><strong>Quelle:</strong> <?=$tm->makeLinkList($quellen, \'quelle_id\', \'kurz\')?></p>\r\n	<?php\r\n}\r\n?>','<p>Artikel, der Detailansicht der <em>Begriffe</em> enthält:</p>\r\nREX_LINK[id=1 widget=1]','2020-05-26 14:44:42','Thomas','2021-03-03 14:58:22','Thomas',NULL,0),
  (8,'tth_view_table','Tabellenansicht','<?php\r\n$tm = new \\kwd\\tth\\TableManager();\r\n\r\n$prefix = $tm->getTablePrefix();\r\n$table = \'\';\r\n\r\n// !!! centralized object which contains all information about table schemas and addtional infos such as custom field description names\r\n//     - should use information from YFORM!! (because you have almost all the info there, additionals could go into rex-config)\r\nswitch(\'REX_VALUE[1]\') {\r\n	case \'Autoren\' : \r\n		// /// try to get around without using table name strings directly \r\n		$table = $prefix.\'autoren\';\r\n		// !!! adjustable by input later\r\n		$order = \'name\';\r\n	break;\r\n	case \'Begriffsstati\' : \r\n		$table = $prefix.\'begriffsstati\';\r\n		// !!! adjustable by input later\r\n		$order = \'status\';\r\n	break;\r\n	case \'Quellen\' : \r\n		$table = $prefix.\'quellen\';\r\n		// !!! adjustable by input later\r\n		$order = \'kurz\';\r\n	break;\r\n	case \'Sprachen\' : \r\n		$table = $prefix.\'sprachen\';\r\n		// !!! adjustable by input later\r\n		$order = \'sprache\';\r\n	break;\r\n	case \'Regionen\' : \r\n		$table = $prefix.\'regionen\';\r\n		// !!! adjustable by input later\r\n		$order = \'region\';\r\n	break;\r\n	case \'Sprachstile\' : \r\n		$table = $prefix.\'sprachstile\';\r\n		$order = \'stil\';\r\n	break;\r\n}\r\n\r\nif ($table) {\r\n	echo \'<h2>REX_VALUE[1]</h2>\';\r\n	$sql = rex_sql::factory();\r\n	$query = \"SELECT * FROM $table WHERE 1 ORDER BY $order ASC\";\r\n	$rows = $sql->getArray($query);\r\n	// dump($rows);\r\n\r\n	$html = \'\';\r\n\r\n	// !!! need options\r\n	//     - define white-space: nowrap for some columns OR at least column id as CSS class for each cell\r\n	//     - hide some cols\r\n	//     - custom names for field names\r\n	if (count($rows)) {\r\n\r\n		$html = \'<table class=\"table table-responsive\">\';\r\n\r\n		foreach($rows[0] as $key => $value) {\r\n			$html .= \'<th>\'.$key.\'</th>\';\r\n		}\r\n\r\n		foreach($rows as $a) {\r\n			$html .= \"<tr>\";\r\n			\r\n			foreach($a as $key => $value) {			\r\n				$html .= \'<td>\'.$value.\'</td>\';\r\n			}\r\n			\r\n			$html .= \"</tr>\\n\";\r\n		}\r\n\r\n		$html .= \'</table>\';\r\n	}\r\n\r\n	echo $html;\r\n}\r\nelse {\r\n	echo rex_view::warning(\'Keine gültige Tabelle ausgewählt.\');\r\n}\r\n?>','Tabelle anzeigen:\r\n<div class=\"rex-select-style\">\r\n<select id=\"REX_INPUT_VALUE[1]\" name=\"REX_INPUT_VALUE[1]\" class=\"form-control\">\r\n<?php\r\n// ... don\'t like rex_select of MForm anymore\r\n\r\n$tables = array(\r\n	\'Autoren\',\r\n	\'Begriffsstati\',\r\n	\'Quellen\',\r\n	\'Regionen\',\r\n	\'Sprachen\',\r\n	\'Sprachstile\'\r\n);\r\n\r\n$preSelected = \'REX_VALUE[1]\';\r\n\r\nforeach($tables as $t) {\r\n	echo \"<option value=\'$t\' \".($preSelected == $t ? \" selected=\'selected\'\":\"\").\">$t</option>\\n\";\r\n}\r\n?>\r\n</select>\r\n</div>','2020-05-26 20:08:16','Thomas','2020-06-26 12:00:18','Thomas',NULL,0),
  (9,'tth_convert_sources','Konvertiere Quellen','<?php if (rex::isBackend()) : ?>\r\nDer Callback heißt \'convertSourcesCallback\'. Du brauchst einen YForm-Block zum Starten.\r\n<?php endif; \r\n\r\nif (!function_exists(\'convertSourcesCallback\')) {\r\n	function convertSourcesCallback($request) {\r\n		// echo \'absenden erkannt\';\r\n		// - convert from quellen_idlist and quelle_seite; easier than from created relations table\r\n		// - preserve order in list\r\n		// - assume page number to belong to first quelle\r\n		// - assume first quelle to be \"preferred\"\r\n		$sql = rex_sql::factory();\r\n		$query = \"SELECT id,quelle_seite,quellen_idlist FROM tth_wortliste WHERE quellen_idlist != \'\'\";\r\n		$rows=$sql->getArray($query);\r\n		echo count($rows) .\" Einträge mit Quellen.\\n\";\r\n\r\n		$insertList = \'\';\r\n		foreach($rows as $r) {\r\n			$sources = explode(\';\',$r[\'quellen_idlist\']);\r\n			// echo \', \'.count($sources);\r\n			$i = 0;\r\n			$seite = $r[\'quelle_seite\'];\r\n			if (!$seite) $seite = \"\'\'\";\r\n\r\n			foreach($sources as $s) {\r\n				if (trim($s)) {\r\n					// echo \'.\';\r\n					// order: quelle_id, seitenzahl, bevorzugt, begriff_id\r\n					// $insertList .= \"(\'.$s.\',\'.$node.\"),\\n\"; check if \\n needed!\r\n					$insertList .= \"($s, \".($i === 0 ? $seite : \"\'\'\").\", \".($i === 0 ? \"\'TRUE\'\" : \"\'\'\").\", ${r[\'id\']}), \";\r\n					$i++;\r\n				}\r\n			}\r\n		}\r\n\r\n		// remove last comma!\r\n		$insertList = substr($insertList,0,strrpos($insertList,\',\'));\r\n		$query = \'INSERT INTO tth_quellenangaben (quelle_id, seitenzahl, bevorzugt, begriff_id) VALUES \'.$insertList;\r\n		echo \'<h3>SQL-Befehl</h3><p>\'.$query.\'</p>\';\r\n		\r\n		// disabled for security\r\n		// $sql->setQuery($query);\r\n	}\r\n}\r\n\r\nif (rex_post(\'FORM\')) {\r\n}\r\nelse {\r\n	$sql = rex_sql::factory();\r\n	// \"SUM\" can be passed a simpler expression than \"COUNT\"\r\n	$query = \"SELECT COUNT(id) AS numEntities, SUM(quellen_idlist != \'\') AS numSources, SUM(quellen_idlist LIKE \'%;%\') AS numMultipleSources, SUM(quelle_seite != \'\') AS numPageNumbers FROM tth_wortliste\";\r\n	$sql->setQuery($query);\r\n	$rows=$sql->getArray();\r\n	$d = $rows[0]\r\n?>\r\n<p>\r\n	Einträge: <?=$d[\'numEntities\']?><br>\r\n	Einträge mit Quellen: <?=$d[\'numSources\']?><br>\r\n	Einträge mit mehreren Quellen: <?=$d[\'numMultipleSources\']?><br>\r\n	Einträge mit Seitenzahlen: <?=$d[\'numPageNumbers\']?>\r\n</p>\r\n\r\n<?php } ?>','Keine Einstellungen einfach speichern.','2020-06-10 22:20:45','Thomas','2020-06-11 00:25:35','Thomas',NULL,0),
