  (15,NULL,'Begriffe für Schlagwort','<?php\r\n    // !!! should go into my helper class\r\n    if (!function_exists(\'getFieldById\')) {\r\n          // best array_find function better than using array_filter which can not stop on find!\r\n        // function array_search_func(array $arr, $func) {\r\n        //     foreach ($arr as $key => $v)\r\n        //         if ($func($v))\r\n        //             return $key;\r\n\r\n        //     return false;\r\n        // }\r\n        function getFieldById(array $arr, $id, $fieldName) {\r\n            foreach ($arr as $key => $v) {\r\n                if ($v[\'id\'] == $id)\r\n                return $v[$fieldName];\r\n            }\r\n\r\n            return false;\r\n        }\r\n    }\r\n\r\n    // !!! provide centralized name string base!\r\n    $tagsTable = \'tth_tags\';\r\n    $tagsRelationTable = \'tth_begriff_tags\';\r\n\r\n    $sql = rex_sql::factory();\r\n\r\n    $listQuery = \"SELECT id, name FROM $tagsTable ORDER BY name ASC\";\r\n    $tagsArray = $sql->getArray($listQuery);\r\n\r\n    // makes clickable List:\r\n    $tm = new \\kwd\\tth\\TableManager();\r\n    $tag = \'Schlagwort\';\r\n\r\n    if ($tagsArray && count($tagsArray)) {\r\n        echo \'<p>\' . $tm->makeLinkList($tagsArray, \'tag_id\', \'name\') .\'</p>\';\r\n    }\r\n\r\n    if (rex_request(\'tag_id\')) {\r\n        $id = rex_escape(rex_request(\'tag_id\',\'string\'));\r\n        // for($i=0; $<count($))\r\n        // echo \"id: $id\";\r\n        $tag = getFieldById($tagsArray, $id, \'name\');\r\n\r\n        $tableEntities = \'tth_wortliste\';\r\n        $tableRelationTags = \'tth_begriff_tags\';\r\n\r\n        // !!! inner join of outer relation (just like in `DetailAnsicht`)\r\n        //     should go into tablemanager!!\r\n        // !!! could make \"getOuterRelation in table manager\r\n        $entitiesQuery = \"SELECT r.tag_id, r.begriff_id, e.begriff, e.id \"; // e.id for output below in makeLinkList\r\n        $entitiesQuery.= \"FROM $tableRelationTags r \";\r\n        $entitiesQuery.= \"JOIN $tableEntities e ON r.begriff_id = e.id \";\r\n        $entitiesQuery.= \"WHERE r.tag_id=$id ORDER BY e.begriff ASC\";\r\n        $entities = $sql->getArray($entitiesQuery);\r\n        // dump($entities);\r\n        if (count($entities)) {\r\n            // dump($entities);\r\n            echo \"<h3>Begriffe, die \\\"$tag\\\" enthalten</h3>\";\r\n            echo \'<p>\'.$tm->makeLinkList($entities, \'begriff_id\', \'begriff\',\"REX_LINK[id=1 output=id]\").\'</p>\';\r\n        }\r\n        else {\r\n            echo \"<p>Keine Begriffe mit \\\"$tag\\\" gefunden.\";\r\n        }\r\n\r\n        echo \"<hr><p>Begriffe, bei denen \\\"Kategorie\\\" gesetzt ist</p>\";\r\n        $katEntitiesQuery = \"SELECT id, begriff FROM $tableEntities WHERE kategorie = \'TRUE\' ORDER BY begriff ASC\";\r\n        $kats = $sql->getArray($katEntitiesQuery);\r\n        echo \'<p>\'.$tm->makeLinkList($kats, \'begriff_id\', \'begriff\',\"REX_LINK[id=1 output=id]\").\'</p>\';\r\n    }\r\n  ?>','<p>Zielseite für <b>Detailansicht</b> der Begriffe:</p>\r\nREX_LINK[id=1 widget=1]\r\n<p>Ausgabe erzeugt:</p>\r\n<ul>\r\n<li>eine Liste aller Schlagwörter, wenn keine ID \"tag_id\" in URL gefunden,</li>\r\n<li>eine Liste aller Entitäten für ein Schlagwort, definiert durch \"tag_id\"</li> \r\n</ul>','2021-03-03 15:01:08','Thomas','2021-03-03 15:01:08','Thomas',NULL,0),
  (16,NULL,'Hierachie der Begriffe','<h2>Hierarchie</h2>\r\n\r\n<?php\r\n    // !!! i    ea for table-names: search in config, when not found load from db\r\n    //     Katze, die sich in den Schwanz beißt\r\n\r\n    $sql = rex_sql::factory();\r\n    // !!! how to store facet id in DB as value, maybe we need a \"config table\"\r\n    $facetArray = $sql->getArray(\"SELECT id, begriff FROM tth_wortliste WHERE begriffsstatus_id = 8\");\r\n    $num = count($facetArray);\r\n    if (!$num) $num = 0; // in case $facetArray === false;\r\n\r\n    $tm = new \\kwd\\tth\\TableManager();\r\n    // !!! since *all* entities follow the hierarchy we could load ALL entities,\r\n    //     and all oberbegriffe/unterbegriffe relations to have it \r\n    $facetId = rex_escape(rex_request(\'facette_id\', \'integer\'));\r\n    if ($facetId) {\r\n        $facetName = \'\';\r\n        foreach($facetArray as $f) {\r\n            if ($f[\'id\'] == $facetId) {\r\n                $facetName = $f[\'begriff\'];\r\n                break;\r\n            }\r\n        }\r\n\r\n        $allOberbegriffeDirectly = $sql->getArray(\r\n            \"SELECT b.begriff,b.id FROM tth_begriff_oberbegriffe o JOIN tth_wortliste b ON b.id = o.begriff_id WHERE oberbegriff_id = $facetId\"\r\n        );\r\n\r\n        if (count($allOberbegriffeDirectly)) {\r\n            echo \"Begriffe für Facette (direkte Nachfahren) \\\"<strong>$facetName</strong>\\\":<br>\";\r\n            echo $tm->makeLinkList($allOberbegriffeDirectly,\'begriff_id\', \'begriff\');\r\n        }\r\n        else {\r\n            echo \"Keine direkten Nachfahren für Facette \\\"<strong>$facetName</strong>\\\".\";\r\n        }\r\n\r\n        // works:\r\n        // $allOberbegriffe = $sql->getArray(\"SELECT begriff_id, oberbegriff_id FROM tth_begriff_oberbegriffe\");\r\n        // $allUnterBegriffe = $sql->getArray(\"SELECT begriff_id, unterbegriff_id FROM tth_begriff_unterbegriffe\");\r\n\r\n        // for compare first find all where nothing set! MUST use OUTER join, wonder why works? see: https://www.xaprb.com/blog/2005/09/23/how-to-write-a-sql-exclusion-join/\r\n        // ! problem: lots of root parents because oberbegriffe *very often* not defined\r\n        $allRootParents = $sql->getArray(\"SELECT b.id, b.begriff FROM tth_wortliste b LEFT OUTER JOIN tth_begriff_oberbegriffe o ON o.begriff_id = b.id WHERE o.begriff_id is NULL\");\r\n        $warnText = \'<p>Es wurden \'.count($allRootParents).\' Entitäten ohne Oberbegriff gefunden.</p>\';\r\n\r\n        $allDeadEnds = $sql->getArray(\"SELECT b.id, b.begriff FROM tth_wortliste b LEFT OUTER JOIN tth_begriff_unterbegriffe u ON u.begriff_id = b.id WHERE u.begriff_id is NULL\");\r\n        $warnText .= \'<p>Es wurden \'.count($allDeadEnds).\' Entitäten ohne Unterbegriff gefunden.</p>\';\r\n        \r\n        $warnText .= \'<p>Die 2. Zahl ist weniger aussagekräftig, da es mehr Unterbegriff-Beziehungen gibt, die bereits durch Oberbegriff-Beziehungen indirekt ausgedrückt sind.</p>\';\r\n        // dump($allRootParents);\r\n        echo rex_view::warning($warnText);\r\n        \r\n    }\r\n    else {\r\n        echo \"<b>Facetten</b> ($num):<br>\";\r\n        echo $tm->makeLinkList($facetArray, \'facette_id\',\'begriff\');  \r\n    \r\n    }\r\n?>\r\n','<!-- Always in my heart, the most wonderful creature on this planet. -->','2021-03-03 15:01:08','Thomas','2021-03-03 15:01:08','Thomas',NULL,0),
  (17,NULL,'Glossar','<?php\r\n    $sql = rex_sql::factory();\r\n    $query = \"SELECT * from tth_glossar WHERE 1 ORDER BY wort ASC\";\r\n    $entries = $sql->getArray($query);\r\n?>\r\n\r\n<table class=\"table table-responsive\">\r\n    <?php\r\n    foreach($entries as $e):\r\n    ?>\r\n    <tr>\r\n        <td>\r\n            <a id=\"<?=rex_escape($e[\'Anker\'])?>\"></a>\r\n            <h4><?=$e[\'wort\']?></h4>\r\n        </td>\r\n        <td>\r\n            <p><?=$e[\'beschreibung\']?></p>\r\n        </td>\r\n    </tr>\r\n    <?php endforeach; ?>\r\n</table>','Keine Einstellungen. Einfach hinzufügen!','2021-03-03 15:01:08','Thomas','2021-03-03 15:11:35','Thomas',NULL,0);
