INSERT INTO `tth_tabellennamen` VALUES 
  (1,'tth_wortliste','Wortliste','Enthält die eigentlichen Entitäten, der Einfachheit halber \"Begriffe\" genannt. Viele Selbst-Beziehungen und externe Beziehungen. Es existieren verschiedene Hilfs-Tabellen (Beziehungs-Tabellen). ','1,2'),
  (2,'tth_quellenangaben','','Definiert eine Quelle für einen Begriff (oder später eine Teilkomponente wie die \"Definition\", \"Benutze\" oder andere Beziehungen)','1'),
  (3,'tth_quellen','Tab_Quellen','Eine Quelle zeigt auf einen oder mehrere Autoren und kann von Quellenangaben verwendet wird. Eine Quelle wird *nicht mehr* direkt von einem Begriff referenziert.','2,5'),
  (4,'tth_autoren','Tab_Autor','Ein Autor gehört zu einer oder mehreren Quellen (n:m-Beziehung). Deshalb wird Beziehungstabelle benötigt.','5'),
  (5,'tth_quellen_autoren','','Beziehungstabelle für die n:m-Beziehung vno Quellen und Autoren.','3,4');
INSERT INTO `tth_wortliste` VALUES 
  (645,'Abbund','bzw. das Abbinden, ist das maßgerechte Anreißen, Bearbeiten, Zusammenpassen und Kennzeichnen von Schnitt- und Rundholz für Tragwerke, Bauteile und Einbauteile.','',2,'',2,'','values.jpg',0,'','',1,0,0,'TRUE','FALSE','FALSE','',427,'2;4;5;7;8;11','','','1059','647','954','1060;1305;1524'),
  (646,'Abbinden (hart werden)','Vorgang bei dem durch chemische Reaktion pastöse oder flüssige Stoffe (z.B. Beton oder Leim ), zuerst erstarren und später ihre Endfestigkeit erreichen.','',1,'',2,'','',0,'','',1,0,0,'FALSE','TRUE','FALSE','',0,'','','','1058','','',''),
  (647,'Abbundzeichen','sind alle Zeichen und Symbole die beim Abbund verwendet werden, also Anrisse und Ausführungszeichen so wie eingeschlagene Zeichen und Symbole die für das Richten der Konstruktion von Bedeutung sind. Abbundzeichen werden regional auch als Scharfzeichnung oder scharfe Nummerung bezeichnet.','',1,'',2,'','',0,'','',1,0,0,'FALSE','FALSE','FALSE','',429,'2;4;5;6;9;11','','','645','661;662','645',''),
  (648,'Abfangen','ist das Aufnehmen von Lasten durch Tragwerke ( Träger-, Joch- oder Strebekonstruktion). Bei Umbauten auch das vorrübergehende Abstützen von Bauteilen.','',0,'',4,'','',0,'','',1,0,0,'FALSE','FALSE','FALSE','',0,'2','','','1058','','',''),
  (649,'Abhängen','','',0,'',1,'','',0,'','',1,0,0,'FALSE','FALSE','FALSE','',0,'','','','1058','','',''),
  (650,'Abhänger','','',0,'',1,'','',0,'','',1,0,0,'FALSE','FALSE','FALSE','',0,'','','','1020','','',''),
  (651,'Abschnittsriß','ein durch Kreuze (mindestens 2 pro Seite) gekennzeichneter und oft umlaufender Riss','',1,'',2,'','',0,'','',1,0,0,'FALSE','FALSE','FALSE','',0,'2','','','645','','661',''),
  (652,'Abseite','Niedriges Seitenschiff mit Untersicht des Daches.','',1,'6.11.1',2,'','',0,'','',1,0,0,'FALSE','FALSE','FALSE','',0,'4;5;6;8','','','956','802;992','1286',''),
  (653,'Abstecken','Festlegen der Lage von Bauwerksfluchten in Gelände mittels Schnurgerüst und Fluchtschnur. Bei längenbetonten Bauwerken oft aus der Mittelachse heraus.','',1,'',4,'','',0,'','',1,0,0,'FALSE','FALSE','FALSE','',0,'','','','1058','','',''),
  (654,'Achsriß','Bezugslinie zum Abtragen von Maßen und für das Richten. Der Achsriß wird gekennzeichnet durch ein M oder durch ein Kreutz mit Fähnchen (gekreuzte Äxte) auf dem Riss.','',1,'',2,'','',0,'','',1,0,0,'FALSE','FALSE','FALSE','',0,'2','','651;700;712;744;843;845','645','','661',''),
  (655,'Andreaskreuz','Überblattung Zweier Hölzer in X-Form. Das Andreaskreuz dient zur Verstrebung und Aussteifung, aber auch zur Ausschmückung.','',4,'',2,'','',0,'','',1,0,0,'FALSE','FALSE','FALSE','',67,'4;5;6;7;11','933','903','1275','','678',''),
  (656,'Anfallsbinder','ist das Gebinde, in einen Walmdach, an dem die Gratsparren anschlagen','',1,'',2,'','',0,'','',1,0,0,'FALSE','FALSE','FALSE','',0,'','','','956','','692',''),
  (658,'Anfallspunkt','Oberes Ende eines Grates oder einer Kehle. Genauer: der Schnittpunkt aus der Firstlinie (oder Punkt) und der Grat- bzw. Kehllinie(-en).','',1,'',2,'','',0,'','',1,0,0,'FALSE','FALSE','FALSE','',0,'2;5;6;11','','699;760;791;822','719;956','','719',''),
  (659,'Ankerbalken','Balken, der zwei tragende Ständer, durch Zapfenschlösser, Kämme oder Einhälsungen auf Zug verbindet','',1,'2.2.1',2,'','',0,'','',1,0,0,'FALSE','TRUE','FALSE','',0,'5;6;7;11','','785;948;1135','1275','','680','1042'),
